function LiveMarketData({
  filteredCryptoData,
  loading,
  SkeletonCryptoCard,
  getPriceChange,
  animatedPrices,
  setSelectedCrypto,
  setBetType,
}) {
  const navigate = useNavigate();
  const carouselRef = React.useRef();
  const [scrollIndex, setScrollIndex] = React.useState(0);
  const visibleCount = 7; // Show 6-8 coins at once

  // Scroll to index
  const scrollToIndex = (idx) => {
    if (!carouselRef.current) return;
    const card = carouselRef.current.querySelectorAll(".carousel-coin-card")[
      idx
    ];
    if (card) {
      card.scrollIntoView({
        behavior: "smooth",
        inline: "center",
        block: "nearest",
      });
    }
  };
  const handleLeft = () => {
    setScrollIndex((prev) => Math.max(prev - 1, 0));
    scrollToIndex(Math.max(scrollIndex - 1, 0));
  };
  const handleRight = () => {
    setScrollIndex((prev) =>
      Math.min(prev + 1, filteredCryptoData.length - visibleCount)
    );
    scrollToIndex(
      Math.min(scrollIndex + 1, filteredCryptoData.length - visibleCount)
    );
  };

  React.useEffect(() => {
    scrollToIndex(scrollIndex);
    // eslint-disable-next-line
  }, [scrollIndex]);

  return (
    <div className="prices-container content-fade-in" data-active={true}>
      <div className="crypto-carousel">
        <button
          className="carousel-arrow left"
          onClick={handleLeft}
          disabled={scrollIndex === 0}
        >
          <svg
            width="18"
            height="18"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
          >
            <path d="M15 18l-6-6 6-6" />
          </svg>
        </button>
        <div className="carousel-track" ref={carouselRef}>
          {loading ? (
            <Suspense fallback={<div>Loading...</div>}>
              {Array.from({ length: visibleCount }).map((_, i) => (
                <SkeletonCryptoCard key={i} />
              ))}
            </Suspense>
          ) : (
            filteredCryptoData.map((crypto, idx) => {
              const priceChange = getPriceChange(crypto.symbol, crypto.price);
              return (
                <div
                  key={crypto.id}
                  className={`carousel-coin-card`}
                  onClick={() => {
                    setSelectedCrypto(crypto.symbol);
                    navigate("/betting");
                  }}
                >
                  <div className="carousel-coin-icon">
                    {crypto.symbol === "BTC"
                      ? "‚Çø"
                      : crypto.symbol === "ETH"
                      ? "Œû"
                      : crypto.symbol === "SOL"
                      ? "‚óé"
                      : crypto.symbol === "BNB"
                      ? "üü°"
                      : crypto.symbol === "DOGE"
                      ? "√ê"
                      : crypto.symbol === "ADA"
                      ? "‚Ç≥"
                      : crypto.symbol === "XRP"
                      ? "‚úï"
                      : crypto.symbol === "DOT"
                      ? "‚Ä¢"
                      : crypto.symbol === "LTC"
                      ? "≈Å"
                      : crypto.symbol === "AVAX"
                      ? "üóª"
                      : crypto.symbol === "LINK"
                      ? "üîó"
                      : crypto.symbol === "UNI"
                      ? "ü¶Ñ"
                      : crypto.symbol === "MATIC"
                      ? "M"
                      : crypto.symbol === "BCH"
                      ? "…É"
                      : crypto.symbol === "XLM"
                      ? "‚òÖ"
                      : crypto.symbol === "FIL"
                      ? "‚®é"
                      : crypto.symbol === "APT"
                      ? "A"
                      : crypto.symbol === "VET"
                      ? "V"
                      : crypto.symbol === "XMR"
                      ? "…±"
                      : crypto.symbol}
                  </div>
                  <div className="carousel-coin-symbol">{crypto.symbol}</div>
                  <div className="carousel-coin-price">
                    $
                    {Number(crypto.price).toLocaleString(undefined, {
                      maximumFractionDigits: 2,
                    })}
                  </div>
                  {priceChange && (
                    <div
                      className={`carousel-coin-change ${priceChange.direction}`}
                    >
                      {priceChange.direction === "up" ? "‚ñ≤" : "‚ñº"}{" "}
                      {priceChange.percentage}%
                    </div>
                  )}
                </div>
              );
            })
          )}
        </div>
        <button
          className="carousel-arrow right"
          onClick={handleRight}
          disabled={scrollIndex >= filteredCryptoData.length - visibleCount}
        >
          <svg
            width="18"
            height="18"
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
          >
            <path d="M9 18l6-6-6-6" />
          </svg>
        </button>
      </div>
    </div>
  );
}

