  });

  const { data: userStats } = useQuery(GET_USER_STATS, {
    variables: { userId },
    pollInterval: 10000,
    errorPolicy: "all",
  });

  const { data: activeBetsData } = useQuery(GET_ACTIVE_BETS, {
    variables: { userId },
    pollInterval: 5000,
    errorPolicy: "all",
  });

  const { data: betHistoryData, loading: betHistoryLoading } = useQuery(
    GET_BET_HISTORY,
    {
      variables: { userId },
      pollInterval: 10000,
      errorPolicy: "all",
    }
  );

  const [placeBet] = useMutation(PLACE_BET, {
    refetchQueries: [
      { query: GET_USER_STATS, variables: { userId } },
      { query: GET_ACTIVE_BETS, variables: { userId } },
    ],
    onError: (error) => {
      console.error("Bet placement error:", error);
    },
  });

  // Optimized animated prices with useMemo
  const animatedPrices = useMemo(() => {
    if (!data?.cryptoPrices) return [];
    return data.cryptoPrices.map((crypto) => crypto.price);
  }, [data?.cryptoPrices]);

  // Memoized filtered crypto data
  const filteredCryptoData = useMemo(() => {
    if (!data?.cryptoPrices) return [];
    return data.cryptoPrices.filter(
      (crypto) =>
        searchQuery === "" ||
        crypto.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        crypto.symbol.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [data?.cryptoPrices, searchQuery]);

  // Optimized handlers with useCallback
  const handleRefresh = useCallback(async () => {
    performance.mark("refresh-start");
    setIsRefreshing(true);
    try {
      await refetch();
    } catch (error) {
      console.error("Refresh error:", error);
    } finally {
      setTimeout(() => {
        setIsRefreshing(false);
        performance.mark("refresh-end");
        performance.measure("refresh-duration", "refresh-start", "refresh-end");
      }, 1000);
    }
  }, [refetch]);

  const handlePlaceBet = useCallback(async () => {
    try {
      await placeBet({
        variables: {
          input: {
            cryptoSymbol: selectedCrypto,
            betType: betType,
            amount: parseFloat(betAmount),
            timeframe: selectedTimeframe,
          },
        },
      });
      setShowBetModal(false);
      setBetAmount(100);
    } catch (error) {
      console.error("Error placing bet:", error);
      alert(error.message);
    }
  }, [placeBet, selectedCrypto, betType, betAmount, selectedTimeframe]);

  const getPriceChange = useCallback(
    (symbol, currentPrice) => {
      const previousPrice = previousPrices[symbol];
      if (!previousPrice || previousPrice === currentPrice) return null;
      return {
        direction: currentPrice > previousPrice ? "up" : "down",
        percentage: (
          ((currentPrice - previousPrice) / previousPrice) *
          100
        ).toFixed(2),
      };
    },
    [previousPrices]
  );

  // Preload critical resources
  useEffect(() => {
    const preloadImage = new Image();
    preloadImage.src = `${process.env.PUBLIC_URL}/images/backgroundImage.png`;
  }, []);

  // Optimized price tracking
  useEffect(() => {
    if (data?.cryptoPrices) {
      setPreviousPrices((prev) => {
        const newPrices = {};
        data.cryptoPrices.forEach((crypto) => {
          newPrices[crypto.symbol] = prev[crypto.symbol] || crypto.price;
        });
        return newPrices;
      });
    }
  }, [data]);

  // Set default selected crypto when coins load
  useEffect(() => {
    if (!selectedCrypto && coinsData?.coins?.length > 0) {
      setSelectedCrypto(coinsData.coins[0].symbol);
    }
  }, [coinsData, selectedCrypto]);

  // Helper for timer with memoization
  const getTimeLeft = useCallback((expiresAt) => {
    const now = new Date();
    const expiry = new Date(expiresAt);
    const diff = expiry - now;
    if (diff <= 0) return "Expired";
    const min = Math.floor(diff / 60000);
    const sec = Math.floor((diff % 60000) / 1000);
    return `${min}:${sec.toString().padStart(2, "0")}`;
  }, []);

  const getCurrentCrypto = useCallback(() => {
    return data?.cryptoPrices?.find(
      (crypto) => crypto.symbol === selectedCrypto
    );
  }, [data, selectedCrypto]);

  const getSelectedTimeframeInfo = useCallback(() => {
    return TIMEFRAMES.find((tf) => tf.value === selectedTimeframe);
  }, [selectedTimeframe]);

  // Timer update for Active Bets
  const [, setTimerTick] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setTimerTick((tick) => tick + 1);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Error handling
  if (error && !data) {
    return (
      <div className={`app ${isDarkMode ? "dark" : "light"}`}>
        <div className="error-container">
          <div className="kokitzu-logo">Kokitzu</div>
          <div className="error-message">
            <h2>Connection Error</h2>
            <p>{error.message}</p>
            <button onClick={handleRefresh} className="retry-button">
              Retry Connection
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <ErrorBoundary>
        <div style={backgroundStyle}>
          <div
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              width: "100vw",
              height: "100vh",
              zIndex: 0,
              pointerEvents: "none",
              background: isDarkMode
                ? "rgba(0,0,0,0.5)"
                : "rgba(255,255,255,0.9)",
            }}
          />
          {/* <KokitzuBackground /> */}
          <div className={`app ${isDarkMode ? "dark" : "light"}`}>
            {/* Navigation */}
            <nav className="nav">
              <div className="nav-content">
                <div className="nav-left">
                  <div className="kokitzu-logo">Kokitzu</div>
                  <div className="brand-subtitle">Crypto Options Trading</div>
                </div>
                <div className="nav-divider" />
                <div className="nav-right">
                  <button
                    className={`refresh-button ${
                      isRefreshing ? "refreshing" : ""
                    }`}
                    onClick={handleRefresh}
                    title="Refresh data"
                  >
                    <svg
                      width="18"
                      height="18"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M23 4v6h-6M1 20v-6h6M20.49 9A9 9 0 0 0 5.64 5.64L1 10m22 4l-4.64 4.36A9 9 0 0 1 3.51 15" />
                    </svg>
                  </button>

                  <button
                    className="theme-toggle"
                    onClick={() => setIsDarkMode(!isDarkMode)}
                    title={`Switch to ${isDarkMode ? "light" : "dark"} mode`}
                  >
                    {isDarkMode ? (
                      <svg
                        width="18"
                        height="18"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                      >
                        <circle cx="12" cy="12" r="5" />
                        <path d="M12 1v2M12 21v2M4.22 4.22l1.42 1.42M18.36 18.36l1.42 1.42M1 12h2M21 12h2M4.22 19.78l1.42-1.42M18.36 5.64l1.42-1.42" />
                      </svg>
                    ) : (
                      <svg
                        width="18"
                        height="18"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                      >
                        <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z" />
                      </svg>
                    )}
                  </button>

                  <div className="user-profile">
                    <div className="user-avatar">üë§</div>
                    <div className="user-info">
                      <span className="username">Trader</span>
                      <span className="user-balance">
                        $
                        {userStats?.userStats?.balance !== undefined
                          ? userStats.userStats.balance.toLocaleString()
                          : (() => {
                              // If no userStats, estimate balance from net profit
                              let netProfit = 0;
                              if (betHistoryData?.betHistory?.length > 0) {
                                // Calculate net profit from bet history
                                netProfit = betHistoryData.betHistory.reduce(
                                  (acc, bet) => {
                                    if (bet.result === "WIN") {
                                      return acc + (bet.payout - bet.amount);
                                    } else if (bet.result === "LOSS") {
                                      return acc - bet.amount;
                                    }
                                    return acc;
                                  },
                                  0
                                );
                              }
                              return (10000 + netProfit).toLocaleString();
                            })()}
                      </span>
                    </div>
                  </div>

                  <button
                    className="mobile-nav-toggle"
                    onClick={() => setIsMobileNavOpen(!isMobileNavOpen)}
                    aria-label="Toggle mobile navigation"
                  >
                    <svg
                      width="24"
                      height="24"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M3 12h18M3 6h18M3 18h18" />
                    </svg>
                  </button>
                </div>
              </div>

              <div className={`nav-menu ${isMobileNavOpen ? "open" : ""}`}>
                <div className="tab-navigation">
                  <Link
                    className={`tab-button ${
                      window.location.pathname === "/" ? "active" : ""
                    }`}
                    to="/"
                    onClick={() => setActiveTab("prices")}
                  >
                    <svg
                      width="16"
                      height="16"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M3 3v18h18M18.7 8l-5.1 5.2-2.8-2.7L7 14.3" />
                    </svg>
                    Live Prices
                  </Link>
                  <Link
                    className={`tab-button ${
                      window.location.pathname === "/betting" ? "active" : ""
                    }`}
                    to="/betting"
                    onClick={() => setActiveTab("betting")}
                  >
                    <svg
                      width="16"
                      height="16"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
                    </svg>
                    Binary Options
                  </Link>
                  <Link
                    className={`tab-button ${
                      window.location.pathname === "/portfolio" ? "active" : ""
                    }`}
                    to="/portfolio"
                    onClick={() => setActiveTab("portfolio")}
                  >
                    <svg
                      width="16"
                      height="16"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
                    </svg>
                    Portfolio
                  </Link>
                </div>
              </div>
            </nav>

            {/* Main Content */}
            <main className="main-content">
              <div className="breadcrumb">
                <span className="breadcrumb-item">Kokitzu</span>
                <span className="breadcrumb-item active">
                  {window.location.pathname === "/" && "Live Prices"}
                  {window.location.pathname === "/betting" && "Binary Options"}
                  {window.location.pathname === "/portfolio" && "Portfolio"}
                </span>
              </div>

              {/* Search Functionality */}
              {window.location.pathname === "/" && (
                <div className="search-container">
                  <svg
                    className="search-icon"
                    width="20"
                    height="20"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                  >
                    <circle cx="11" cy="11" r="8" />
                    <path d="M21 21l-4.35-4.35" />
                  </svg>
                  <input
                    type="text"
                    className="search-input"
                    placeholder="Search cryptocurrencies..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </div>
              )}

              <Routes>
                <Route
                  path="/"
                  element={
                    <LiveMarketData
                      filteredCryptoData={filteredCryptoData}
                      loading={loading}
                      SkeletonCryptoCard={SkeletonCryptoCard}
                      getPriceChange={getPriceChange}
                      animatedPrices={animatedPrices}
                      setSelectedCrypto={setSelectedCrypto}
                      setBetType={setBetType}
                    />
                  }
                />
                <Route
                  path="/betting"
                  element={
                    <BinaryOptions
                      coinsData={coinsData}
                      data={data}
                      loading={loading}
                      SkeletonCryptoCard={SkeletonCryptoCard}
                      selectedCrypto={selectedCrypto}
                      setSelectedCrypto={setSelectedCrypto}
                      selectedTimeframe={selectedTimeframe}
                      setSelectedTimeframe={setSelectedTimeframe}
                      betAmount={betAmount}
                      setBetAmount={setBetAmount}
                      betType={betType}
                      setBetType={setBetType}
                      getSelectedTimeframeInfo={getSelectedTimeframeInfo}
                      handlePlaceBet={handlePlaceBet}
                      showBetModal={showBetModal}
                      setShowBetModal={setShowBetModal}
                      getPriceChange={getPriceChange}
                      animatedPrices={animatedPrices}
                      activeBetsData={activeBetsData}
                      getTimeLeft={getTimeLeft}
                      currentPrice={getCurrentCrypto()?.price}
                    />
                  }
                />
                <Route
                  path="/portfolio"
                  element={
                    <Portfolio
                      userStats={userStats}
                      betHistoryLoading={betHistoryLoading}
                      betHistoryData={betHistoryData}
                      getTimeLeft={getTimeLeft}
                      currentPrice={getCurrentCrypto()?.price}
                    />
                  }
                />
              </Routes>
            </main>

            {/* Bet Confirmation Modal */}
            {showBetModal && (
              <div
                className="modal-overlay"
                onClick={() => setShowBetModal(false)}
              >
                <div
                  className="modal-content"
                  onClick={(e) => e.stopPropagation()}
                >
                  <h3>Confirm Your Bet</h3>
                  <div className="bet-confirmation">
                    <div className="confirmation-item">
                      <span>Cryptocurrency:</span>
                      <span>{selectedCrypto}</span>
                    </div>
                    <div className="confirmation-item">
                      <span>Direction:</span>
                      <span className={`direction ${betType.toLowerCase()}`}>
                        {betType}
                      </span>
                    </div>
                    <div className="confirmation-item">
                      <span>Timeframe:</span>
                      <span>{getSelectedTimeframeInfo()?.label}</span>
                    </div>
                    <div className="confirmation-item">
                      <span>Amount:</span>
                      <span>${betAmount}</span>
                    </div>
                    <div className="confirmation-item">
                      <span>Potential Payout:</span>
                      <span>
                        $
                        {(
                          betAmount *
                          parseFloat(
                            getSelectedTimeframeInfo()?.payout.replace("x", "")
                          )
                        ).toFixed(2)}
                      </span>
                    </div>
                  </div>
                  <div className="modal-actions">
                    <button
                      className="cancel-btn"
                      onClick={() => setShowBetModal(false)}
                    >
                      Cancel
                    </button>
                    <button className="confirm-btn" onClick={handlePlaceBet}>
                      Confirm Bet
                    </button>
                  </div>
                </div>
              </div>
            )}

            {/* Footer */}
            <footer className="footer">
              <div className="footer-content">
                <div className="footer-links">
                  <span>Powered by CoinGecko</span>
                  <span>‚Ä¢</span>
                  <span>Binary Options Trading</span>
                </div>
              </div>
            </footer>
          </div>
        </div>
      </ErrorBoundary>
    </Router>
  );
}

export default App;

function PerformanceChart({ bets }) {
  // Sort bets by date
  const sorted = [...bets].sort(
    (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
  );
  // Calculate cumulative profit
  let cumulative = 0;
  const points = sorted.map((bet, i) => {
    const profit = bet.result === "WIN" ? bet.payout - bet.amount : -bet.amount;
    cumulative += profit;
    return { x: i, y: cumulative, date: bet.createdAt, profit };
  });
  if (points.length < 2) {
    // Not enough data for a line
    return (
      <svg width="100%" height="300" viewBox="0 0 400 300">
        <text x="50%" y="50%" textAnchor="middle" fill="#888" fontSize="18">
          Not enough data
        </text>
      </svg>
    );
  }
  // Scale points to SVG
  const maxY = Math.max(...points.map((p) => p.y), 0);
  const minY = Math.min(...points.map((p) => p.y), 0);
  const rangeY = maxY - minY || 1;
  const chartW = 380,
    chartH = 260,
    padX = 10,
    padY = 20;
  const scaleX = (i) => padX + i * (chartW / (points.length - 1));
  const scaleY = (y) => padY + chartH - ((y - minY) / rangeY) * chartH;
  const pathD = points
    .map((p, i) => `${i === 0 ? "M" : "L"}${scaleX(i)},${scaleY(p.y)}`)
    .join(" ");
  return (
    <svg width="100%" height="300" viewBox={`0 0 400 300`}>
      <defs>
        <linearGradient id="chartGradient" x1="0%" y1="0%" x2="0%" y2="100%">
          <stop
            offset="0%"
            stopColor="var(--accent-primary)"
            stopOpacity="0.3"
          />
          <stop
            offset="100%"
            stopColor="var(--accent-primary)"
            stopOpacity="0"
          />
        </linearGradient>
      </defs>
      <path
        d={pathD}
        stroke="var(--accent-primary)"
        strokeWidth="3"
        fill="none"
        className="chart-line"
      />
      <polyline
        points={points.map((p, i) => `${scaleX(i)},${scaleY(p.y)}`).join(" ")}
        fill="url(#chartGradient)"
        opacity="0.1"
        className="chart-area"
      />
      {points.map((p, i) => (
        <circle
          key={i}
          cx={scaleX(i)}
          cy={scaleY(p.y)}
          r="4"
          className="data-point"
        />
      ))}
      {/* Y axis labels */}
      <text x={padX} y={scaleY(maxY) - 5} fill="#aaa" fontSize="12">
        {maxY.toFixed(2)}
      </text>
      <text x={padX} y={scaleY(minY) + 15} fill="#aaa" fontSize="12">
        {minY.toFixed(2)}
      </text>
    </svg>
  );
}
