// Animated number hook
function useAnimatedNumber(value, duration = 600) {
  const [display, setDisplay] = useState(value);
  useEffect(() => {
    let start = null;
    let frame;
    const initial = display;
    const delta = value - initial;
    if (delta === 0) return;
    function animate(ts) {
      if (!start) start = ts;
      const progress = Math.min((ts - start) / duration, 1);
      setDisplay(initial + delta * progress);
      if (progress < 1) {
        frame = requestAnimationFrame(animate);
      } else {
        setDisplay(value);
      }
    }
    frame = requestAnimationFrame(animate);
    return () => cancelAnimationFrame(frame);
    // eslint-disable-next-line
  }, [value]);
  return display;
}

// Custom hook for a list of animated numbers
function useAnimatedNumberList(values, duration = 600) {
  return values.map((v, i) => useAnimatedNumber(v, duration));
}

