                            stopColor="var(--accent-primary)"
                            stopOpacity="0.3"
                          />
                          <stop
                            offset="100%"
                            stopColor="var(--accent-primary)"
                            stopOpacity="0"
                          />
                        </linearGradient>
                      </defs>
                    </svg>
                  </div>
                </div>
              </div>
              <div
                className="bet-history-section"
                style={{ marginTop: "2rem" }}
              >
                <h3 style={{ marginBottom: "1rem" }}>Bet History</h3>
                {betHistoryLoading ? (
                  <div>Loading bet history...</div>
                ) : betHistoryData?.betHistory?.length > 0 ? (
                  <table
                    style={{
                      width: "100%",
                      background: "var(--bg-card)",
                      borderRadius: "12px",
                      overflow: "hidden",
                      fontSize: "0.95rem",
                    }}
                  >
                    <thead>
                      <tr style={{ background: "var(--bg-card-hover)" }}>
                        <th style={{ padding: "8px" }}>Crypto</th>
                        <th>Direction</th>
                        <th>Amount</th>
                        <th>Timeframe</th>
                        <th>Result</th>
                        <th>Payout</th>
                        <th>Date</th>
                      </tr>
                    </thead>
                    <tbody>
                      {betHistoryData.betHistory.map((bet) => (
                        <tr key={bet.id}>
                          <td style={{ padding: "8px" }}>{bet.cryptoSymbol}</td>
                          <td>{bet.betType}</td>
                          <td>${bet.amount}</td>
                          <td>{bet.timeframe.replace("_", " ")}</td>
                          <td
                            style={{
                              color:
                                bet.result === "WIN"
                                  ? "var(--accent-success)"
                                  : "var(--accent-error)",
                            }}
                          >
                            {bet.result || bet.status}
                          </td>
                          <td>
                            {bet.payout !== null
                              ? `$${bet.payout.toFixed(2)}`
                              : "-"}
                          </td>
                          <td>{new Date(bet.createdAt).toLocaleString()}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                ) : (
                  <div>No bet history yet.</div>
                )}
              </div>
            </div>
          )}
        </main>

        {/* Bet Confirmation Modal */}
        {showBetModal && (
          <div className="modal-overlay" onClick={() => setShowBetModal(false)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <h3>Confirm Your Bet</h3>
              <div className="bet-confirmation">
                <div className="confirmation-item">
                  <span>Cryptocurrency:</span>
                  <span>{selectedCrypto}</span>
                </div>
                <div className="confirmation-item">
                  <span>Direction:</span>
                  <span className={`direction ${betType.toLowerCase()}`}>
                    {betType}
                  </span>
                </div>
                <div className="confirmation-item">
                  <span>Timeframe:</span>
                  <span>{getSelectedTimeframeInfo()?.label}</span>
                </div>
                <div className="confirmation-item">
                  <span>Amount:</span>
                  <span>${betAmount}</span>
                </div>
                <div className="confirmation-item">
                  <span>Potential Payout:</span>
                  <span>
                    $
                    {(
                      betAmount *
                      parseFloat(
                        getSelectedTimeframeInfo()?.payout.replace("x", "")
                      )
                    ).toFixed(2)}
                  </span>
                </div>
              </div>
              <div className="modal-actions">
                <button
                  className="cancel-btn"
                  onClick={() => setShowBetModal(false)}
                >
                  Cancel
                </button>
                <button className="confirm-btn" onClick={handlePlaceBet}>
                  Confirm Bet
                </button>
              </div>
            </div>
          </div>
        )}

        {/* Footer */}
        <footer className="footer">
          <div className="footer-content">
            <div className="footer-links">
              <span>Powered by CoinGecko</span>
              <span>•</span>
              <span>Binary Options Trading</span>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
}

export default App;
