import React, {
  useState,
  useEffect,
  useCallback,
  useMemo,
  Suspense,
  lazy,
} from "react";
import { useQuery, useMutation, gql } from "@apollo/client";
import "./App.css";
// import KokitzuBackground from "./KokitzuBackground";

// Lazy load components for better performance
const SkeletonCryptoCard = lazy(() =>
  import("./components/SkeletonCryptoCard")
);
const SkeletonStats = lazy(() => import("./components/SkeletonStats"));

// Error Boundary Component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error("Error caught by boundary:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-boundary">
          <h2>Something went wrong</h2>
          <p>Please refresh the page or try again later.</p>
          <button onClick={() => window.location.reload()}>Refresh Page</button>
        </div>
      );
    }

    return this.props.children;
  }
}

// Performance monitoring hook
const usePerformanceMonitor = () => {
  useEffect(() => {
    if (process.env.NODE_ENV === "development") {
      const observer = new PerformanceObserver((list) => {
        for (const entry of list.getEntries()) {
          if (entry.entryType === "measure") {
            console.log(`${entry.name}: ${entry.duration}ms`);
          }
        }
      });
      observer.observe({ entryTypes: ["measure"] });
      return () => observer.disconnect();
    }
  }, []);
};

// Optimized animated number hook
const useOptimizedAnimatedNumber = (value, duration = 600) => {
  const [display, setDisplay] = useState(value);

  useEffect(() => {
    let raf;
    const start = performance.now();
    const initial = display;
    const delta = value - initial;

    if (delta === 0) return;

    const animate = (now) => {
      const progress = Math.min((now - start) / duration, 1);
      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      setDisplay(initial + delta * easeOutCubic);

      if (progress < 1) {
        raf = requestAnimationFrame(animate);
      } else {
        setDisplay(value);
      }
    };

    raf = requestAnimationFrame(animate);
    return () => raf && cancelAnimationFrame(raf);
  }, [value, duration]);

  return display;
};

const GET_CRYPTO_PRICES = gql`
  query GetCryptoPrices {
    cryptoPrices {
      id
      symbol
      name
      price
      lastUpdated
    }
  }
`;

const GET_COINS = gql`
  query GetCoins {
    coins {
      id
      symbol
      name
    }
  }
`;

const GET_USER_STATS = gql`
  query GetUserStats($userId: String!) {
    userStats(userId: $userId) {
      totalBets
      wins
      losses
      winRate
      totalWagered
      totalWon
      netProfit
    }
  }
`;

const GET_ACTIVE_BETS = gql`
  query GetActiveBets($userId: String!) {
    activeBets(userId: $userId) {
      id
      cryptoSymbol
      betType
      amount
      timeframe
      entryPrice
      status
      createdAt
      expiresAt
    }
  }
`;

const GET_BET_HISTORY = gql`
  query GetBetHistory($userId: String!) {
    betHistory(userId: $userId) {
      id
      cryptoSymbol
      betType
      amount
      timeframe
      entryPrice
      status
      createdAt
      expiresAt
      result
      payout
    }
  }
`;

const PLACE_BET = gql`
  mutation PlaceBet($input: PlaceBetInput!) {
    placeBet(input: $input) {
      id
      cryptoSymbol
      betType
      amount
      timeframe
      entryPrice
      status
      createdAt
      expiresAt
    }
  }
`;

const TIMEFRAMES = [
  { value: "ONE_MINUTE", label: "1 Min", payout: "1.8x" },
  { value: "FIVE_MINUTES", label: "5 Min", payout: "1.9x" },
  { value: "FIFTEEN_MINUTES", label: "15 Min", payout: "2.0x" },
  { value: "THIRTY_MINUTES", label: "30 Min", payout: "2.1x" },
  { value: "ONE_HOUR", label: "1 Hour", payout: "2.2x" },
  { value: "FOUR_HOURS", label: "4 Hours", payout: "2.5x" },
  { value: "ONE_DAY", label: "1 Day", payout: "3.0x" },
];

const backgroundStyle = {
  backgroundImage: `url(${process.env.PUBLIC_URL}/images/backgroundImage.png)`,
  backgroundSize: "cover",
  backgroundPosition: "center",
  backgroundRepeat: "no-repeat",
  minHeight: "100vh",
};

function App() {
  // Performance monitoring
  usePerformanceMonitor();

  // Optimized state management
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [previousPrices, setPreviousPrices] = useState({});
  const [selectedCrypto, setSelectedCrypto] = useState(null);
  const [selectedTimeframe, setSelectedTimeframe] = useState("FIVE_MINUTES");
  const [betAmount, setBetAmount] = useState(100);
  const [betType, setBetType] = useState("UP");
  const [showBetModal, setShowBetModal] = useState(false);
  const [activeTab, setActiveTab] = useState("prices");
  const [searchQuery, setSearchQuery] = useState("");
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);

  const userId = "user-1";

  // Optimized queries with better caching
  const { loading, error, data, refetch } = useQuery(GET_CRYPTO_PRICES, {
    pollInterval: 30000,
    notifyOnNetworkStatusChange: true,
    errorPolicy: "all",
  });

  const { data: coinsData, loading: coinsLoading } = useQuery(GET_COINS, {
    errorPolicy: "all",
  });

  const { data: userStats } = useQuery(GET_USER_STATS, {
    variables: { userId },
    pollInterval: 10000,
    errorPolicy: "all",
  });

  const { data: activeBetsData } = useQuery(GET_ACTIVE_BETS, {
    variables: { userId },
    pollInterval: 5000,
    errorPolicy: "all",
  });

  const { data: betHistoryData, loading: betHistoryLoading } = useQuery(
    GET_BET_HISTORY,
    {
      variables: { userId },
      pollInterval: 10000,
      errorPolicy: "all",
    }
  );

  const [placeBet] = useMutation(PLACE_BET, {
    refetchQueries: [
      { query: GET_USER_STATS, variables: { userId } },
      { query: GET_ACTIVE_BETS, variables: { userId } },
    ],
    onError: (error) => {
      console.error("Bet placement error:", error);
    },
  });

  // Optimized animated prices with useMemo
  const animatedPrices = useMemo(() => {
    if (!data?.cryptoPrices) return [];
    return data.cryptoPrices.map((crypto) =>
      useOptimizedAnimatedNumber(crypto.price)
    );
  }, [data?.cryptoPrices]);

  // Memoized filtered crypto data
  const filteredCryptoData = useMemo(() => {
    if (!data?.cryptoPrices) return [];
    return data.cryptoPrices.filter(
      (crypto) =>
        searchQuery === "" ||
        crypto.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        crypto.symbol.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [data?.cryptoPrices, searchQuery]);

  // Optimized handlers with useCallback
  const handleRefresh = useCallback(async () => {
    performance.mark("refresh-start");
    setIsRefreshing(true);
    try {
      await refetch();
    } catch (error) {
      console.error("Refresh error:", error);
    } finally {
      setTimeout(() => {
        setIsRefreshing(false);
        performance.mark("refresh-end");
        performance.measure("refresh-duration", "refresh-start", "refresh-end");
      }, 1000);
    }
  }, [refetch]);

  const handlePlaceBet = useCallback(async () => {
    try {
      await placeBet({
        variables: {
          input: {
            cryptoSymbol: selectedCrypto,
            betType: betType,
            amount: parseFloat(betAmount),
            timeframe: selectedTimeframe,
          },
        },
      });
      setShowBetModal(false);
      setBetAmount(100);
    } catch (error) {
      console.error("Error placing bet:", error);
      alert(error.message);
    }
  }, [placeBet, selectedCrypto, betType, betAmount, selectedTimeframe]);

  const getPriceChange = useCallback(
    (symbol, currentPrice) => {
      const previousPrice = previousPrices[symbol];
      if (!previousPrice || previousPrice === currentPrice) return null;
      return {
        direction: currentPrice > previousPrice ? "up" : "down",
        percentage: (
          ((currentPrice - previousPrice) / previousPrice) *
          100
        ).toFixed(2),
      };
    },
    [previousPrices]
  );

  // Preload critical resources
  useEffect(() => {
    const preloadImage = new Image();
    preloadImage.src = `${process.env.PUBLIC_URL}/images/backgroundImage.png`;
  }, []);

  // Optimized price tracking
  useEffect(() => {
    if (data?.cryptoPrices) {
      setPreviousPrices((prev) => {
        const newPrices = {};
        data.cryptoPrices.forEach((crypto) => {
          newPrices[crypto.symbol] = prev[crypto.symbol] || crypto.price;
        });
        return newPrices;
      });
    }
  }, [data]);

  // Set default selected crypto when coins load
  useEffect(() => {
    if (!selectedCrypto && coinsData?.coins?.length > 0) {
      setSelectedCrypto(coinsData.coins[0].symbol);
    }
  }, [coinsData, selectedCrypto]);

  // Helper for timer with memoization
  const getTimeLeft = useCallback((expiresAt) => {
    const now = new Date();
    const expiry = new Date(expiresAt);
    const diff = expiry - now;
    if (diff <= 0) return "Expired";
    const min = Math.floor(diff / 60000);
    const sec = Math.floor((diff % 60000) / 1000);
    return `${min}:${sec.toString().padStart(2, "0")}`;
  }, []);

  const getCurrentCrypto = useCallback(() => {
    return data?.cryptoPrices?.find(
      (crypto) => crypto.symbol === selectedCrypto
    );
  }, [data, selectedCrypto]);

  const getSelectedTimeframeInfo = useCallback(() => {
    return TIMEFRAMES.find((tf) => tf.value === selectedTimeframe);
  }, [selectedTimeframe]);

  // Error handling
  if (error && !data) {
    return (
      <div className={`app ${isDarkMode ? "dark" : "light"}`}>
        <div className="error-container">
          <div className="kokitzu-logo">Kokitzu</div>
          <div className="error-message">
            <h2>Connection Error</h2>
            <p>{error.message}</p>
            <button onClick={handleRefresh} className="retry-button">
              Retry Connection
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <ErrorBoundary>
      <div style={backgroundStyle}>
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            width: "100vw",
            height: "100vh",
            zIndex: 0,
            pointerEvents: "none",
            background: isDarkMode
              ? "rgba(0,0,0,0.5)"
              : "rgba(255,255,255,0.9)",
          }}
        />
        {/* <KokitzuBackground /> */}
        <div className={`app ${isDarkMode ? "dark" : "light"}`}>
          {/* Navigation */}
          <nav className="nav">
            <div className="nav-content">
              <div className="nav-left">
                <div className="kokitzu-logo">Kokitzu</div>
                <div className="brand-subtitle">Crypto Options Trading</div>
              </div>

              <div className="nav-right">
                <button
                  className={`refresh-button ${
                    isRefreshing ? "refreshing" : ""
                  }`}
                  onClick={handleRefresh}
                  title="Refresh data"
                >
                  <svg
                    width="18"
                    height="18"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                  >
                    <path d="M23 4v6h-6M1 20v-6h6M20.49 9A9 9 0 0 0 5.64 5.64L1 10m22 4l-4.64 4.36A9 9 0 0 1 3.51 15" />
                  </svg>
                </button>

                <button
                  className="theme-toggle"
                  onClick={() => setIsDarkMode(!isDarkMode)}
                  title={`Switch to ${isDarkMode ? "light" : "dark"} mode`}
                >
                  {isDarkMode ? (
                    <svg
                      width="18"
                      height="18"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <circle cx="12" cy="12" r="5" />
                      <path d="M12 1v2M12 21v2M4.22 4.22l1.42 1.42M18.36 18.36l1.42 1.42M1 12h2M21 12h2M4.22 19.78l1.42-1.42M18.36 5.64l1.42-1.42" />
                    </svg>
                  ) : (
                    <svg
                      width="18"
                      height="18"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z" />
                    </svg>
                  )}
                </button>

                <div className="user-profile">
                  <div className="user-avatar">👤</div>
                  <div className="user-info">
                    <span className="username">Trader</span>
                    <span className="user-balance">
                      $
                      {userStats?.userStats?.balance?.toLocaleString() ||
                        "10,000"}
                    </span>
                  </div>
                </div>

                <button
                  className="mobile-nav-toggle"
                  onClick={() => setIsMobileNavOpen(!isMobileNavOpen)}
                  aria-label="Toggle mobile navigation"
                >
                  <svg
                    width="24"
                    height="24"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                  >
                    <path d="M3 12h18M3 6h18M3 18h18" />
                  </svg>
                </button>
              </div>
            </div>

            <div className={`nav-menu ${isMobileNavOpen ? "open" : ""}`}>
              <div className="tab-navigation">
                <button
                  className={`tab-button ${
                    activeTab === "prices" ? "active" : ""
                  }`}
                  onClick={() => {
                    setActiveTab("prices");
                    setIsMobileNavOpen(false);
                  }}
                >
                  <svg
                    width="16"
                    height="16"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                  >
                    <path d="M3 3v18h18M18.7 8l-5.1 5.2-2.8-2.7L7 14.3" />
                  </svg>
                  Live Prices
                </button>
                <button
                  className={`tab-button ${
                    activeTab === "betting" ? "active" : ""
                  }`}
                  onClick={() => {
                    setActiveTab("betting");
                    setIsMobileNavOpen(false);
                  }}
                >
                  <svg
                    width="16"
                    height="16"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                  >
                    <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
                  </svg>
                  Binary Options
                </button>
                <button
                  className={`tab-button ${
                    activeTab === "portfolio" ? "active" : ""
                  }`}
                  onClick={() => {
                    setActiveTab("portfolio");
                    setIsMobileNavOpen(false);
                  }}
                >
                  <svg
                    width="16"
                    height="16"
                    viewBox="0 0 24 24"
                    fill="none"
                    stroke="currentColor"
                    strokeWidth="2"
                  >
                    <path d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
                  </svg>
                  Portfolio
                </button>
              </div>
            </div>
          </nav>

          {/* Tab Navigation */}
          <div className="tab-navigation">
            <button
              className={`tab-button ${activeTab === "prices" ? "active" : ""}`}
              onClick={() => setActiveTab("prices")}
            >
              <svg
                width="16"
                height="16"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
              >
                <path d="M3 3v18h18M18.7 8l-5.1 5.2-2.8-2.7L7 14.3" />
              </svg>
              Live Prices
            </button>
            <button
              className={`tab-button ${
                activeTab === "betting" ? "active" : ""
              }`}
              onClick={() => setActiveTab("betting")}
            >
              <svg
                width="16"
                height="16"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
              >
                <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
              </svg>
              Binary Options
            </button>
            <button
              className={`tab-button ${
                activeTab === "portfolio" ? "active" : ""
              }`}
              onClick={() => setActiveTab("portfolio")}
            >
              <svg
                width="16"
                height="16"
                viewBox="0 0 24 24"
                fill="none"
                stroke="currentColor"
                strokeWidth="2"
              >
                <path d="M20 7l-8-4-8 4m16 0l-8 4m8-4v10l-8 4m0-10L4 7m8 4v10M4 7v10l8 4" />
              </svg>
              Portfolio
            </button>
          </div>

          {/* Main Content */}
          <main className="main-content">
            {/* Breadcrumb Navigation */}
            <div className="breadcrumb">
              <span className="breadcrumb-item">Kokitzu</span>
              <span className="breadcrumb-item active">
                {activeTab === "prices" && "Live Prices"}
                {activeTab === "betting" && "Binary Options"}
                {activeTab === "portfolio" && "Portfolio"}
              </span>
            </div>

            {/* Search Functionality */}
            {activeTab === "prices" && (
              <div className="search-container">
                <svg
                  className="search-icon"
                  width="20"
                  height="20"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                >
                  <circle cx="11" cy="11" r="8" />
                  <path d="M21 21l-4.35-4.35" />
                </svg>
                <input
                  type="text"
                  className="search-input"
                  placeholder="Search cryptocurrencies..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </div>
            )}

            {activeTab === "prices" && (
              <div
                className="prices-container content-fade-in"
                data-active={activeTab === "prices"}
              >
                <div className="prices-header">
                  <h1>Live Market Data</h1>
                  <p>Real-time cryptocurrency prices powered by GraphQL</p>
                </div>
                <div className="crypto-grid">
                  {loading ? (
                    <Suspense fallback={<div>Loading...</div>}>
                      {Array.from({ length: 6 }).map((_, i) => (
                        <SkeletonCryptoCard key={i} />
                      ))}
                    </Suspense>
                  ) : (
                    filteredCryptoData.map((crypto, idx) => {
                      const priceChange = getPriceChange(
                        crypto.symbol,
                        crypto.price
                      );
                      return (
                        <div key={crypto.id} className="crypto-card">
                          <div className="card-header">
                            <div className="crypto-info">
                              <div className="crypto-icon">
                                {/* Use a generic icon or emoji for now, or add a mapping for more coins */}
                                {crypto.symbol === "BTC"
                                  ? "₿"
                                  : crypto.symbol === "ETH"
                                  ? "Ξ"
                                  : crypto.symbol === "SOL"
                                  ? "◎"
                                  : crypto.symbol === "BNB"
                                  ? "🟡"
                                  : crypto.symbol === "DOGE"
                                  ? "Ð"
                                  : crypto.symbol === "ADA"
                                  ? "₳"
                                  : crypto.symbol === "XRP"
                                  ? "✕"
                                  : crypto.symbol === "DOT"
                                  ? "•"
                                  : crypto.symbol === "LTC"
                                  ? "Ł"
                                  : crypto.symbol === "AVAX"
                                  ? "🗻"
                                  : crypto.symbol === "LINK"
                                  ? "🔗"
                                  : crypto.symbol === "UNI"
                                  ? "🦄"
                                  : crypto.symbol === "MATIC"
                                  ? "M"
                                  : crypto.symbol === "BCH"
                                  ? "Ƀ"
                                  : crypto.symbol === "XLM"
                                  ? "★"
                                  : crypto.symbol === "FIL"
                                  ? "⨎"
                                  : crypto.symbol === "APT"
                                  ? "A"
                                  : crypto.symbol === "VET"
                                  ? "V"
                                  : crypto.symbol === "XMR"
                                  ? "ɱ"
                                  : crypto.symbol}
                              </div>
                              <div className="crypto-details">
                                <h2>{crypto.name}</h2>
                                <span className="symbol">{crypto.symbol}</span>
                              </div>
                            </div>

                            {priceChange && (
                              <div
                                className={`price-change ${priceChange.direction}`}
                              >
                                <svg
                                  width="14"
                                  height="14"
                                  viewBox="0 0 24 24"
                                  fill="none"
                                  stroke="currentColor"
                                  strokeWidth="2"
                                >
                                  {priceChange.direction === "up" ? (
                                    <path d="M18 15l-6-6-6 6" />
                                  ) : (
                                    <path d="M6 9l6 6 6-6" />
                                  )}
                                </svg>
                                <span>{priceChange.percentage}%</span>
                              </div>
                            )}
                          </div>

                          <div className="price-display">
                            <div className="price-amount">
                              $
                              {(
                                animatedPrices[idx] ?? crypto.price
                              ).toLocaleString(undefined, {
                                minimumFractionDigits: 2,
                                maximumFractionDigits: 2,
                              })}
                            </div>
                            <div className="price-decimal">
                              {/* Decimal part is now included in price-amount for animation */}
                            </div>
                          </div>

                          <div className="card-footer">
                            <div className="last-updated">
                              <div className="status-dot"></div>
                              <span className="real-time-indicator">Live</span>
                              {new Date(
                                crypto.lastUpdated
                              ).toLocaleTimeString()}
                            </div>
                          </div>

                          <div className="sparkline">
                            <svg width="100%" height="30" viewBox="0 0 100 30">
                              <path
                                d="M0,15 Q25,5 50,15 T100,15"
                                stroke="currentColor"
                                strokeWidth="1.5"
                                fill="none"
                                className="sparkline-path"
                              />
                            </svg>
                          </div>
                        </div>
                      );
                    })
                  )}
                </div>
              </div>
            )}

            {activeTab === "betting" && (
              <div
                className="betting-container"
                data-active={activeTab === "betting"}
              >
                <div className="betting-header">
                  <h1>Binary Options Trading</h1>
                  <p>Bet on price direction within your chosen timeframe</p>
                </div>

                <div className="betting-layout">
                  {/* Main Betting Form */}
                  <div className="betting-form-section">
                    <div className="form-card">
                      {/* Crypto Selection */}
                      <div className="form-section">
                        <label>Select Cryptocurrency</label>
                        <div className="crypto-selector">
                          {coinsData?.coins?.map((coin) => {
                            const priceObj = data?.cryptoPrices?.find(
                              (c) => c.symbol === coin.symbol
                            );
                            return (
                              <button
                                key={coin.symbol}
                                className={`crypto-option ${
                                  selectedCrypto === coin.symbol
                                    ? "selected"
                                    : ""
                                }`}
                                onClick={() => setSelectedCrypto(coin.symbol)}
                              >
                                <span className="crypto-symbol">
                                  {coin.symbol}
                                </span>
                                <span className="crypto-price">
                                  {priceObj
                                    ? `$${priceObj.price.toLocaleString()}`
                                    : "-"}
                                </span>
                              </button>
                            );
                          })}
                        </div>
                      </div>

                      {/* Bet Direction & Amount Row */}
                      <div className="form-row">
                        <div className="form-section">
                          <label>Bet Direction</label>
                          <div className="bet-direction">
                            <button
                              className={`direction-btn up ${
                                betType === "UP" ? "selected" : ""
                              }`}
                              onClick={() => setBetType("UP")}
                            >
                              <svg
                                width="16"
                                height="16"
                                viewBox="0 0 24 24"
                                fill="none"
                                stroke="currentColor"
                                strokeWidth="2"
                              >
                                <path d="M18 15l-6-6-6 6" />
                              </svg>
                              UP
                            </button>
                            <button
                              className={`direction-btn down ${
                                betType === "DOWN" ? "selected" : ""
                              }`}
                              onClick={() => setBetType("DOWN")}
                            >
                              <svg
                                width="16"
                                height="16"
                                viewBox="0 0 24 24"
                                fill="none"
                                stroke="currentColor"
                                strokeWidth="2"
                              >
                                <path d="M6 9l6 6 6-6" />
                              </svg>
                              DOWN
                            </button>
                          </div>
                        </div>

                        <div className="form-section">
                          <label>Bet Amount</label>
                          <div className="amount-input">
                            <span className="currency">$</span>
                            <input
                              type="number"
                              value={betAmount}
                              onChange={(e) => setBetAmount(e.target.value)}
                              min="10"
                              max="10000"
                              step="10"
                            />
                          </div>
                        </div>
                      </div>

                      {/* Timeframe Selection */}
                      <div className="form-section">
                        <label>Timeframe</label>
                        <div className="timeframe-scroll">
                          <div className="timeframe-selector">
                            {TIMEFRAMES.map((timeframe) => (
                              <button
                                key={timeframe.value}
                                className={`timeframe-option ${
                                  selectedTimeframe === timeframe.value
                                    ? "selected"
                                    : ""
                                }`}
                                onClick={() =>
                                  setSelectedTimeframe(timeframe.value)
                                }
                              >
                                <span className="timeframe-label">
                                  {timeframe.label}
                                </span>
                                <span className="timeframe-payout">
                                  {timeframe.payout}
                                </span>
                              </button>
                            ))}
                          </div>
                        </div>
                      </div>

                      {/* Bet Summary */}
                      <div className="bet-summary">
                        <div className="summary-item">
                          <span>Potential Payout:</span>
                          <span className="payout-amount">
                            $
                            {(
                              betAmount *
                              parseFloat(
                                getSelectedTimeframeInfo()?.payout.replace(
                                  "x",
                                  ""
                                )
                              )
                            ).toFixed(2)}
                          </span>
                        </div>
                        <div className="summary-item">
                          <span>Profit:</span>
                          <span className="profit-amount">
                            $
                            {(
                              betAmount *
                              (parseFloat(
                                getSelectedTimeframeInfo()?.payout.replace(
                                  "x",
                                  ""
                                )
                              ) -
                                1)
                            ).toFixed(2)}
                          </span>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Active Bets Sidebar */}
                  <div className="active-bets-section">
                    <div className="active-bets-card">
                      <div className="card-header">
                        <h3>Active Bets</h3>
                        <span className="bet-count">
                          {activeBetsData?.activeBets?.length || 0}
                        </span>
                      </div>

                      {activeBetsData?.activeBets?.length > 0 ? (
                        <div className="bets-list">
                          {activeBetsData.activeBets.map((bet) => (
                            <div key={bet.id} className="bet-item">
                              <div className="bet-header">
                                <div className="bet-crypto">
                                  {bet.cryptoSymbol}
                                </div>
                                <div
                                  className={`bet-direction ${bet.betType.toLowerCase()}`}
                                >
                                  {bet.betType}
                                </div>
                              </div>
                              <div className="bet-details">
                                <div className="bet-amount">${bet.amount}</div>
                                <div className="bet-timeframe">
                                  {bet.timeframe.replace("_", " ")}
                                </div>
                              </div>
                              <div className="bet-status">
                                <div className="status-indicator active"></div>
                                <span>Active</span>
                                <span
                                  style={{
                                    marginLeft: "auto",
                                    fontWeight: 600,
                                  }}
                                >
                                  {getTimeLeft(bet.expiresAt)}
                                </span>
                              </div>
                            </div>
                          ))}
                        </div>
                      ) : (
                        <div className="no-bets">
                          <svg
                            width="48"
                            height="48"
                            viewBox="0 0 24 24"
                            fill="none"
                            stroke="currentColor"
                            strokeWidth="1"
                          >
                            <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
                          </svg>
                          <p>No active bets</p>
                          <span>Place your first bet to get started</span>
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                {/* Sticky Bottom Bar */}
                <div className="sticky-bottom-bar">
                  <div className="bet-preview">
                    <div className="preview-item">
                      <span>{selectedCrypto}</span>
                      <span className={`direction ${betType.toLowerCase()}`}>
                        {betType}
                      </span>
                    </div>
                    <div className="preview-item">
                      <span>${betAmount}</span>
                      <span className="payout">
                        {getSelectedTimeframeInfo()?.payout}
                      </span>
                    </div>
                  </div>
                  <button
                    className="place-bet-btn"
                    onClick={() => setShowBetModal(true)}
                    disabled={!selectedCrypto || !betType || !betAmount}
                  >
                    Place Bet
                  </button>
                </div>
              </div>
            )}

            {activeTab === "portfolio" && (
              <div
                className="portfolio-container content-fade-in"
                data-active={activeTab === "portfolio"}
              >
                <div className="portfolio-header">
                  <h1>Trading Portfolio</h1>
                  <p>Your trading performance and statistics</p>
                </div>

                <div className="portfolio-layout">
                  {betHistoryLoading ? (
                    <Suspense fallback={<SkeletonStats />}>
                      <SkeletonStats />
                    </Suspense>
                  ) : (
                    <div className="stats-grid">
                      <div className="stat-card">
                        <div className="stat-icon">📊</div>
                        <div className="stat-content">
                          <h3>Total Bets</h3>
                          <div className="stat-value">
                            {userStats?.userStats?.totalBets || 0}
                          </div>
                        </div>
                      </div>
                      <div className="stat-card">
                        <div className="stat-icon">🎯</div>
                        <div className="stat-content">
                          <h3>Win Rate</h3>
                          <div className="stat-value">
                            {(userStats?.userStats?.winRate || 0).toFixed(1)}%
                          </div>
                        </div>
                      </div>
                      <div className="stat-card">
                        <div className="stat-icon">💰</div>
                        <div className="stat-content">
                          <h3>Total Wagered</h3>
                          <div className="stat-value">
                            $
                            {(
                              userStats?.userStats?.totalWagered || 0
                            ).toLocaleString()}
                          </div>
                        </div>
                      </div>
                      <div className="stat-card">
                        <div className="stat-icon">📈</div>
                        <div className="stat-content">
                          <h3>Net Profit</h3>
                          <div
                            className={`stat-value ${
                              (userStats?.userStats?.netProfit || 0) >= 0
                                ? "positive"
                                : "negative"
                            }`}
                          >
                            ${(userStats?.userStats?.netProfit || 0).toFixed(2)}
                          </div>
                        </div>
                      </div>
                    </div>
                  )}

                  <div className="performance-chart">
                    <div className="chart-header">
                      <h3>Performance Overview</h3>
                      <div className="chart-filters">
                        <button className="filter-btn active">7D</button>
                        <button className="filter-btn">30D</button>
                        <button className="filter-btn">90D</button>
                      </div>
                    </div>
                    <div className="chart-placeholder">
                      <svg width="100%" height="300" viewBox="0 0 400 300">
                        <defs>
                          <linearGradient
                            id="chartGradient"
                            x1="0%"
                            y1="0%"
                            x2="0%"
                            y2="100%"
                          >
                            <stop
                              offset="0%"
                              stopColor="var(--accent-primary)"
                              stopOpacity="0.3"
                            />
                            <stop
                              offset="100%"
                              stopColor="var(--accent-primary)"
                              stopOpacity="0"
                            />
                          </linearGradient>
                        </defs>
                        <path
                          d="M0,250 Q50,200 100,220 T200,180 T300,160 T400,140"
                          stroke="var(--accent-primary)"
                          strokeWidth="3"
                          fill="none"
                          className="chart-line"
                        />
                        <path
                          d="M0,250 Q50,200 100,220 T200,180 T300,160 T400,140 L400,300 L0,300 Z"
                          fill="url(#chartGradient)"
                          opacity="0.1"
                          className="chart-area"
                        />
                        {/* Interactive data points */}
                        <circle
                          cx="100"
                          cy="220"
                          r="4"
                          className="data-point"
                        />
                        <circle
                          cx="200"
                          cy="180"
                          r="4"
                          className="data-point"
                        />
                        <circle
                          cx="300"
                          cy="160"
                          r="4"
                          className="data-point"
                        />
                        <circle
                          cx="400"
                          cy="140"
                          r="4"
                          className="data-point"
                        />
                      </svg>
                    </div>
                  </div>
                </div>
                <div
                  className="bet-history-section"
                  style={{ marginTop: "2rem" }}
                >
                  <h3 style={{ marginBottom: "1rem" }}>Bet History</h3>
                  {betHistoryLoading ? (
                    <div className="data-loading">Loading bet history...</div>
                  ) : betHistoryData?.betHistory?.length > 0 ? (
                    <table className="bet-history-table">
                      <thead>
                        <tr>
                          <th>Crypto</th>
                          <th>Direction</th>
                          <th>Amount</th>
                          <th>Timeframe</th>
                          <th>Result</th>
                          <th>Payout</th>
                          <th>Date</th>
                        </tr>
                      </thead>
                      <tbody>
                        {betHistoryData.betHistory.map((bet) => (
                          <tr key={bet.id}>
                            <td>{bet.cryptoSymbol}</td>
                            <td>{bet.betType}</td>
                            <td>${bet.amount}</td>
                            <td>{bet.timeframe.replace("_", " ")}</td>
                            <td
                              className={
                                bet.result === "WIN" ? "positive" : "negative"
                              }
                            >
                              {bet.result || bet.status}
                            </td>
                            <td>
                              {bet.payout !== null
                                ? `$${bet.payout.toFixed(2)}`
                                : "-"}
                            </td>
                            <td>{new Date(bet.createdAt).toLocaleString()}</td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  ) : (
                    <div>No bet history yet.</div>
                  )}
                </div>
              </div>
            )}
          </main>

          {/* Bet Confirmation Modal */}
          {showBetModal && (
            <div
              className="modal-overlay"
              onClick={() => setShowBetModal(false)}
            >
              <div
                className="modal-content"
                onClick={(e) => e.stopPropagation()}
              >
                <h3>Confirm Your Bet</h3>
                <div className="bet-confirmation">
                  <div className="confirmation-item">
                    <span>Cryptocurrency:</span>
                    <span>{selectedCrypto}</span>
                  </div>
                  <div className="confirmation-item">
                    <span>Direction:</span>
                    <span className={`direction ${betType.toLowerCase()}`}>
                      {betType}
                    </span>
                  </div>
                  <div className="confirmation-item">
                    <span>Timeframe:</span>
                    <span>{getSelectedTimeframeInfo()?.label}</span>
                  </div>
                  <div className="confirmation-item">
                    <span>Amount:</span>
                    <span>${betAmount}</span>
                  </div>
                  <div className="confirmation-item">
                    <span>Potential Payout:</span>
                    <span>
                      $
                      {(
                        betAmount *
                        parseFloat(
                          getSelectedTimeframeInfo()?.payout.replace("x", "")
                        )
                      ).toFixed(2)}
                    </span>
                  </div>
                </div>
                <div className="modal-actions">
                  <button
                    className="cancel-btn"
                    onClick={() => setShowBetModal(false)}
                  >
                    Cancel
                  </button>
                  <button className="confirm-btn" onClick={handlePlaceBet}>
                    Confirm Bet
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Footer */}
          <footer className="footer">
            <div className="footer-content">
              <div className="footer-links">
                <span>Powered by CoinGecko</span>
                <span>•</span>
                <span>Binary Options Trading</span>
              </div>
            </div>
          </footer>
        </div>
      </div>
    </ErrorBoundary>
  );
}

export default App;
