import React, { useState } from "react";
import { useWallet } from "../contexts/WalletContext";
import { useNetwork, NETWORKS } from "../contexts/NetworkContext";

const WalletConnectButton = () => {
  const {
    walletAddress,
    isConnected,
    balance,
    connectMetaMask,
    disconnectWallet,
    connectionStatus,
    isMetaMaskInstalled,
  } = useWallet();

  const { currentNetwork, networkConfig, switchNetwork, isNetworkSwitching } =
    useNetwork();
  const [showModal, setShowModal] = useState(false);
  const [showNetworkModal, setShowNetworkModal] = useState(false);

  const handleConnect = async () => {
    try {
      if (!isMetaMaskInstalled()) {
        alert(
          "MetaMask is not installed. Please install MetaMask to continue."
        );
        return;
      }
      await connectMetaMask();
      setShowModal(false);
    } catch (error) {
      console.error("Connection error:", error);
      alert(error.message);
    }
  };

  const handleDisconnect = () => {
    disconnectWallet();
  };

  const handleNetworkSelect = async (networkType) => {
    try {
      setShowNetworkModal(false);
      await switchNetwork(networkType);
    } catch (error) {
      console.error("Network switch error:", error);
      alert(`Failed to switch to ${networkType}: ${error.message}`);
    }
  };

  const formatAddress = (address) => {
    if (!address) return "Unknown";
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  const getNetworkIcon = (network) => {
    switch (network) {
      case "mainnet":
        return "üîµ";
      case "sepolia":
        return "üß™";
      case "goerli":
        return "üß¨";
      default:
        return "üåê";
    }
  };

  const getNetworkColor = (network) => {
    switch (network) {
      case "mainnet":
        return "#10b981";
      case "sepolia":
        return "#3b82f6";
      case "goerli":
        return "#f59e0b";
      default:
        return "#666";
    }
  };

  const getChainName = (chainId) => {
    switch (chainId) {
      case "1":
        return "ETH";
      case "11155111":
        return "Sepolia ETH";
      case "5":
        return "Goerli ETH";
      default:
        return networkConfig.nativeCurrency.symbol;
    }
  };

  // Connected state
  if (isConnected && walletAddress) {
    return (
      <div className="wallet-connect-button connected">
        <div className="wallet-info">
          <div className="wallet-address">{formatAddress(walletAddress)}</div>
          {balance && (
            <div className="wallet-balance">
              {parseFloat(balance).toFixed(4)}{" "}
              {getChainName(networkConfig.chainId)}
            </div>
          )}
        </div>
        <button
          className="disconnect-btn"
          onClick={handleDisconnect}
          title="Disconnect Wallet"
        >
          üîå
        </button>
      </div>
    );
  }

  // Disconnected state
  return (
    <>
      <button
        className="wallet-connect-button"
        onClick={() => setShowModal(true)}
        disabled={connectionStatus === "connecting"}
      >
        {connectionStatus === "connecting" ? "Connecting..." : "Connect Wallet"}
      </button>

      {/* Wallet Connection Modal */}
      {showModal && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div
            className="modal-content wallet-modal"
            onClick={(e) => e.stopPropagation()}
          >
            <div className="modal-header">
              <h3>Connect Wallet</h3>
              <button className="close-btn" onClick={() => setShowModal(false)}>
                ‚úï
              </button>
            </div>

            {/* Network Selection Section */}
            <div className="network-section">
              <h4>Network</h4>
              {!showNetworkModal ? (
                <div
                  className="network-selector"
                  style={{ borderColor: getNetworkColor(currentNetwork) }}
                  onClick={() => setShowNetworkModal(true)}
                >
                  <div className="network-info">
                    <span className="network-icon">
                      {getNetworkIcon(currentNetwork)}
                    </span>
                    <div className="network-details">
                      <div className="network-name">{networkConfig.name}</div>
                    </div>
                  </div>
                  <div className="network-actions">
                    {isNetworkSwitching && <span className="loading">‚è≥</span>}
                    <span className="chevron">‚ñº</span>
                  </div>
                </div>
              ) : (
                <div className="network-list-container">
                  <div className="network-list-header">
                    <h4>Select Network</h4>
                    <button
                      className="close-btn"
                      onClick={() => setShowNetworkModal(false)}
                    >
                      ‚úï
                    </button>
                  </div>
                  <div className="network-list">
                    {Object.entries(NETWORKS).map(([key, network]) => (
                      <div
                        key={key}
                        className={`network-item ${
                          currentNetwork === key ? "selected" : ""
                        }`}
                        onClick={() => handleNetworkSelect(key)}
                      >
                        <div className="network-info">
                          <span className="network-icon">
                            {getNetworkIcon(key)}
                          </span>
                          <div className="network-details">
                            <div className="network-name">{network.name}</div>
                            {network.isTestnet && (
                              <span className="testnet-badge">TESTNET</span>
                            )}
                          </div>
                        </div>
                        {currentNetwork === key && (
                          <span className="check-icon">‚úì</span>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Connection Options */}
            <div className="connection-options">
              <button
                className="connection-option metamask"
                onClick={handleConnect}
                disabled={connectionStatus === "connecting"}
              >
                <span className="option-icon">ü¶ä</span>
                <div className="option-details">
                  <div className="option-title">MetaMask</div>
                  <div className="option-subtitle">Connect with MetaMask</div>
                </div>
              </button>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default WalletConnectButton;
