import React, {
  useState,
  useEffect,
  useCallback,
  useMemo,
  Suspense,
  lazy,
} from "react";
import { useQuery, useMutation, gql } from "@apollo/client";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
} from "react-router-dom";
import "./App.css";
// import KokitzuBackground from "./KokitzuBackground";
import {
  ApolloClient,
  InMemoryCache,
  ApolloProvider,
  createHttpLink,
} from "@apollo/client";
import { setContext } from "@apollo/client/link/context";

// Lazy load components for better performance
const SkeletonCryptoCard = lazy(() =>
  import("./components/SkeletonCryptoCard")
);
const SkeletonStats = lazy(() => import("./components/SkeletonStats"));

// Error Boundary Component
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error("Error caught by boundary:", error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div className="error-boundary">
          <h2>Something went wrong</h2>
          <p>Please refresh the page or try again later.</p>
          <button onClick={() => window.location.reload()}>Refresh Page</button>
        </div>
      );
    }

    return this.props.children;
  }
}

// Performance monitoring hook
const usePerformanceMonitor = () => {
  useEffect(() => {
    if (process.env.NODE_ENV === "development") {
      const observer = new PerformanceObserver((list) => {
        for (const entry of list.getEntries()) {
          if (entry.entryType === "measure") {
            console.log(`${entry.name}: ${entry.duration}ms`);
          }
        }
      });
      observer.observe({ entryTypes: ["measure"] });
      return () => observer.disconnect();
    }
  }, []);
};

// Optimized animated number hook
const useOptimizedAnimatedNumber = (value, duration = 600) => {
  const [display, setDisplay] = useState(value);

  useEffect(() => {
    let raf;
    const start = performance.now();
    const initial = display;
    const delta = value - initial;

    if (delta === 0) return;

    const animate = (now) => {
      const progress = Math.min((now - start) / duration, 1);
      const easeOutCubic = 1 - Math.pow(1 - progress, 3);
      setDisplay(initial + delta * easeOutCubic);

      if (progress < 1) {
        raf = requestAnimationFrame(animate);
      } else {
        setDisplay(value);
      }
    };

    raf = requestAnimationFrame(animate);
    return () => raf && cancelAnimationFrame(raf);
  }, [value, duration]);

  return display;
};

const GET_CRYPTO_PRICES = gql`
  query GetCryptoPrices {
    cryptoPrices {
      id
      symbol
      name
      price
      lastUpdated
    }
  }
`;

const GET_COINS = gql`
  query GetCoins {
    coins {
      id
      symbol
      name
    }
  }
`;

const GET_USER_STATS = gql`
  query GetUserStats($userId: String!) {
    userStats(userId: $userId) {
      totalBets
      wins
      losses
      winRate
      totalWagered
      totalWon
      netProfit
    }
  }
`;

const GET_ACTIVE_BETS = gql`
  query GetActiveBets($userId: String!) {
    activeBets(userId: $userId) {
      id
      cryptoSymbol
      betType
      amount
      timeframe
      entryPrice
      exitPrice
      status
      createdAt
      expiresAt
      result
      payout
    }
  }
`;

const GET_BET_HISTORY = gql`
  query GetBetHistory($userId: String!) {
    betHistory(userId: $userId) {
      id
      cryptoSymbol
      betType
      amount
      timeframe
      entryPrice
      exitPrice
      status
      createdAt
      expiresAt
      result
      payout
    }
  }
`;

const PLACE_BET = gql`
  mutation PlaceBet($input: PlaceBetInput!) {
    placeBet(input: $input) {
      id
      cryptoSymbol
      betType
      amount
      timeframe
      entryPrice
      status
      createdAt
      expiresAt
    }
  }
`;

const REGISTER = gql`
  mutation Register($username: String!, $password: String!) {
    register(username: $username, password: $password) {
      id
      username
      balance
    }
  }
`;
const LOGIN = gql`
  mutation Login($username: String!, $password: String!) {
    login(username: $username, password: $password) {
      token
      user {
        id
        username
        balance
      }
    }
  }
`;

const TIMEFRAMES = [
  { value: "ONE_MINUTE", label: "1 Min", payout: "1.8x" },
  { value: "FIVE_MINUTES", label: "5 Min", payout: "1.9x" },
  { value: "FIFTEEN_MINUTES", label: "15 Min", payout: "2.0x" },
  { value: "THIRTY_MINUTES", label: "30 Min", payout: "2.1x" },
  { value: "ONE_HOUR", label: "1 Hour", payout: "2.2x" },
  { value: "FOUR_HOURS", label: "4 Hours", payout: "2.5x" },
  { value: "ONE_DAY", label: "1 Day", payout: "3.0x" },
];

const backgroundStyle = {
  backgroundImage: `url(${process.env.PUBLIC_URL}/images/backgroundImage.png)`,
  backgroundSize: "cover",
  backgroundPosition: "center",
  backgroundRepeat: "no-repeat",
  minHeight: "100vh",
};

function LiveMarketData({
  filteredCryptoData,
  loading,
  SkeletonCryptoCard,
  getPriceChange,
  animatedPrices,
  setSelectedCrypto,
  setBetType,
}) {
  const navigate = useNavigate();
  return (
    <div className="prices-container content-fade-in" data-active={true}>
      <div className="crypto-grid">
        {loading ? (
          <Suspense fallback={<div>Loading...</div>}>
            {Array.from({ length: 6 }).map((_, i) => (
              <SkeletonCryptoCard key={i} />
            ))}
          </Suspense>
        ) : (
          filteredCryptoData.map((crypto, idx) => {
            const priceChange = getPriceChange(crypto.symbol, crypto.price);
            return (
              <div
                key={crypto.id}
                className="crypto-card"
                style={{ cursor: "default", position: "relative" }}
              >
                <div className="card-main-data">
                  <div className="card-header">
                    <div className="crypto-info">
                      <div className="crypto-icon">
                        {/* Use a generic icon or emoji for now, or add a mapping for more coins */}
                        {crypto.symbol === "BTC"
                          ? "â‚¿"
                          : crypto.symbol === "ETH"
                          ? "Îž"
                          : crypto.symbol === "SOL"
                          ? "â—Ž"
                          : crypto.symbol === "BNB"
                          ? "ðŸŸ¡"
                          : crypto.symbol === "DOGE"
                          ? "Ã"
                          : crypto.symbol === "ADA"
                          ? "â‚³"
                          : crypto.symbol === "XRP"
                          ? "âœ•"
                          : crypto.symbol === "DOT"
                          ? "â€¢"
                          : crypto.symbol === "LTC"
                          ? "Å"
                          : crypto.symbol === "AVAX"
                          ? "ðŸ—»"
                          : crypto.symbol === "LINK"
                          ? "ðŸ”—"
                          : crypto.symbol === "UNI"
                          ? "ðŸ¦„"
                          : crypto.symbol === "MATIC"
                          ? "M"
                          : crypto.symbol === "BCH"
                          ? "Éƒ"
                          : crypto.symbol === "XLM"
                          ? "â˜…"
                          : crypto.symbol === "FIL"
                          ? "â¨Ž"
                          : crypto.symbol === "APT"
                          ? "A"
                          : crypto.symbol === "VET"
                          ? "V"
                          : crypto.symbol === "XMR"
                          ? "É±"
                          : crypto.symbol}
                      </div>
                      <div className="crypto-details">
                        <h2>{crypto.name}</h2>
                        <span className="symbol">{crypto.symbol}</span>
                      </div>
                    </div>

                    {priceChange && (
                      <div className={`price-change ${priceChange.direction}`}>
                        <svg
                          width="14"
                          height="14"
                          viewBox="0 0 24 24"
                          fill="none"
                          stroke="currentColor"
                          strokeWidth="2"
                        >
                          {priceChange.direction === "up" ? (
                            <path d="M18 15l-6-6-6 6" />
                          ) : (
                            <path d="M6 9l6 6 6-6" />
                          )}
                        </svg>
                        <span>{priceChange.percentage}%</span>
                      </div>
                    )}
                  </div>

                  <div className="price-display">
                    <div className="price-amount">
                      $
                      {(animatedPrices[idx] ?? crypto.price).toLocaleString(
                        undefined,
                        {
                          minimumFractionDigits: 2,
                          maximumFractionDigits: 2,
                        }
                      )}
                    </div>
                    <div className="price-decimal">
                      {/* Decimal part is now included in price-amount for animation */}
                    </div>
                  </div>

                  <div className="card-footer">
                    <div className="last-updated">
                      <div className="status-dot"></div>
                      <span className="real-time-indicator">Live</span>
                      {new Date(crypto.lastUpdated).toLocaleTimeString()}
                    </div>
                  </div>
                </div>

                <div className="sparkline">
                  <svg width="100%" height="30" viewBox="0 0 100 30">
                    <path
                      d="M0,15 Q25,5 50,15 T100,15"
                      stroke="currentColor"
                      strokeWidth="1.5"
                      fill="none"
                      className="sparkline-path"
                    />
                  </svg>
                </div>
                <div
                  style={{
                    position: "absolute",
                    right: 12,
                    bottom: 12,
                    display: "flex",
                    flexDirection: "column",
                    gap: 8,
                    zIndex: 2,
                  }}
                >
                  <button
                    className="arrow-btn"
                    style={{
                      background: "rgba(16,185,129,0.15)",
                      border: "none",
                      borderRadius: 8,
                      padding: 6,
                      marginBottom: 2,
                      cursor: "pointer",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      boxShadow: "0 2px 8px rgba(16,185,129,0.08)",
                      transition: "background 0.2s",
                    }}
                    title="Bet UP"
                    onClick={() => {
                      setSelectedCrypto(crypto.symbol);
                      // Assuming setBetType is passed as a prop or available in scope
                      setBetType && setBetType("UP");
                      navigate("/betting");
                    }}
                  >
                    <svg
                      width="20"
                      height="20"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="var(--accent-success)"
                      strokeWidth="2"
                    >
                      <path d="M18 15l-6-6-6 6" />
                    </svg>
                  </button>
                  <button
                    className="arrow-btn down"
                    style={{
                      background: "rgba(239,68,68,0.15)",
                      border: "none",
                      borderRadius: 8,
                      padding: 6,
                      cursor: "pointer",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      boxShadow: "0 2px 8px rgba(239,68,68,0.08)",
                      transition: "background 0.2s",
                    }}
                    title="Bet DOWN"
                    onClick={() => {
                      setSelectedCrypto(crypto.symbol);
                      // Assuming setBetType is passed as a prop or available in scope
                      setBetType && setBetType("DOWN");
                      navigate("/betting");
                    }}
                  >
                    <svg
                      width="20"
                      height="20"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="var(--accent-error)"
                      strokeWidth="2"
                    >
                      <path d="M6 9l6 6 6-6" />
                    </svg>
                  </button>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

function BinaryOptions({
  coinsData,
  data,
  loading,
  SkeletonCryptoCard,
  selectedCrypto,
  setSelectedCrypto,
  selectedTimeframe,
  setSelectedTimeframe,
  betAmount,
  setBetAmount,
  betType,
  setBetType,
  getSelectedTimeframeInfo,
  handlePlaceBet,
  showBetModal,
  setShowBetModal,
  getPriceChange,
  animatedPrices,
  activeBetsData,
  getTimeLeft,
  currentPrice,
}) {
  const navigate = useNavigate();
  return (
    <div className="betting-container" data-active={true}>
      <div className="betting-layout">
        {/* Main Betting Form */}
        <div className="betting-form-section">
          <div className="form-card">
            {/* Crypto Selection */}
            <div className="form-section">
              <label>Select Cryptocurrency</label>
              <div className="crypto-selector">
                {coinsData?.coins?.map((coin) => {
                  const priceObj = data?.cryptoPrices?.find(
                    (c) => c.symbol === coin.symbol
                  );
                  return (
                    <button
                      key={coin.symbol}
                      className={`crypto-option ${
                        selectedCrypto === coin.symbol ? "selected" : ""
                      }`}
                      onClick={() => setSelectedCrypto(coin.symbol)}
                    >
                      <span className="crypto-symbol">{coin.symbol}</span>
                      <span className="crypto-price">
                        {priceObj ? `$${priceObj.price.toLocaleString()}` : "-"}
                      </span>
                    </button>
                  );
                })}
              </div>
            </div>

            {/* Bet Direction & Amount Row */}
            <div className="form-row">
              <div className="form-section">
                <label>Bet Direction</label>
                <div className="bet-direction">
                  <button
                    className={`direction-btn up ${
                      betType === "UP" ? "selected" : ""
                    }`}
                    onClick={() => setBetType("UP")}
                  >
                    <svg
                      width="16"
                      height="16"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M18 15l-6-6-6 6" />
                    </svg>
                    UP
                  </button>
                  <button
                    className={`direction-btn down ${
                      betType === "DOWN" ? "selected" : ""
                    }`}
                    onClick={() => setBetType("DOWN")}
                  >
                    <svg
                      width="16"
                      height="16"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M6 9l6 6 6-6" />
                    </svg>
                    DOWN
                  </button>
                </div>
              </div>

              <div className="form-section">
                <label>Bet Amount</label>
                <div className="amount-input">
                  <span className="currency">$</span>
                  <input
                    type="number"
                    value={betAmount}
                    onChange={(e) => setBetAmount(e.target.value)}
                    min="10"
                    max="10000"
                    step="10"
                  />
                </div>
              </div>
            </div>

            {/* Timeframe Selection */}
            <div className="form-section">
              <label>Timeframe</label>
              <div className="timeframe-scroll">
                <div className="timeframe-selector">
                  {TIMEFRAMES.map((timeframe) => (
                    <button
                      key={timeframe.value}
                      className={`timeframe-option ${
                        selectedTimeframe === timeframe.value ? "selected" : ""
                      }`}
                      onClick={() => setSelectedTimeframe(timeframe.value)}
                    >
                      <span className="timeframe-label">{timeframe.label}</span>
                      <span className="timeframe-payout">
                        {timeframe.payout}
                      </span>
                    </button>
                  ))}
                </div>
              </div>
            </div>

            {/* Bet Summary */}
            <div className="bet-summary">
              <div className="summary-item">
                <span>Potential Payout:</span>
                <span className="payout-amount">
                  $
                  {(
                    betAmount *
                    parseFloat(
                      getSelectedTimeframeInfo()?.payout.replace("x", "")
                    )
                  ).toFixed(2)}
                </span>
              </div>
              <div className="summary-item">
                <span>Profit:</span>
                <span className="profit-amount">
                  $
                  {(
                    betAmount *
                    (parseFloat(
                      getSelectedTimeframeInfo()?.payout.replace("x", "")
                    ) -
                      1)
                  ).toFixed(2)}
                </span>
              </div>
            </div>

            {/* Bet Action Bar */}
            <div className="bet-action-bar">
              <div className="bet-preview">
                <div className="preview-item">
                  <span>{selectedCrypto}</span>
                  <span className={`direction ${betType.toLowerCase()}`}>
                    {betType}
                  </span>
                </div>
                <div className="preview-item">
                  <span>${betAmount}</span>
                  <span className="payout">
                    {getSelectedTimeframeInfo()?.payout}
                  </span>
                </div>
              </div>
              <button
                className="place-bet-btn"
                onClick={() => setShowBetModal(true)}
                disabled={!selectedCrypto || !betType || !betAmount}
              >
                Place Bet
              </button>
            </div>
          </div>
        </div>

        {/* Active Bets Sidebar */}
        <div className="active-bets-section">
          <div className="active-bets-card">
            <div className="card-header">
              <h3>Active Bets</h3>
              <span className="bet-count">
                {activeBetsData?.activeBets?.length || 0}
              </span>
            </div>

            {activeBetsData?.activeBets?.length > 0 ? (
              <div className="bets-list">
                {activeBetsData.activeBets.map((bet) => {
                  // Find the current price for this crypto symbol
                  const currentCrypto = data?.cryptoPrices?.find(
                    (c) => c.symbol === bet.cryptoSymbol
                  );
                  const currentPrice = currentCrypto
                    ? currentCrypto.price
                    : undefined;
                  const isFinished = bet.status !== "ACTIVE";
                  const comparePrice = isFinished
                    ? bet.exitPrice
                    : currentPrice;
                  let profitState = null;
                  if (
                    comparePrice !== undefined &&
                    bet.entryPrice !== undefined
                  ) {
                    if (bet.betType === "UP") {
                      if (comparePrice > bet.entryPrice) profitState = "profit";
                      else if (comparePrice < bet.entryPrice)
                        profitState = "loss";
                      else profitState = "neutral";
                    } else if (bet.betType === "DOWN") {
                      if (comparePrice < bet.entryPrice) profitState = "profit";
                      else if (comparePrice > bet.entryPrice)
                        profitState = "loss";
                      else profitState = "neutral";
                    }
                  }
                  // Overlay style
                  let overlayStyle = {};
                  if (profitState === "profit") {
                    overlayStyle = {
                      background: "rgba(16,185,129,0.15)",
                      boxShadow: "0 0 16px 0 rgba(16,185,129,0.15)",
                      backdropFilter: "blur(2px)",
                      border: "1.5px solid var(--profit)",
                    };
                  } else if (profitState === "loss") {
                    overlayStyle = {
                      background: "rgba(239,68,68,0.15)",
                      boxShadow: "0 0 16px 0 rgba(239,68,68,0.15)",
                      backdropFilter: "blur(2px)",
                      border: "1.5px solid var(--loss)",
                    };
                  }
                  // Price color
                  let priceColor = undefined;
                  if (profitState === "profit")
                    priceColor = "var(--accent-success)";
                  else if (profitState === "loss")
                    priceColor = "var(--accent-error)";
                  return (
                    <div key={bet.id} className="bet-item" style={overlayStyle}>
                      <div className="bet-header">
                        <div className="bet-crypto">{bet.cryptoSymbol}</div>
                        <div
                          className={`bet-direction ${bet.betType.toLowerCase()}`}
                        >
                          {bet.betType}
                        </div>
                      </div>
                      <div className="bet-details">
                        <div className="bet-amount">${bet.amount}</div>
                        <div className="bet-timeframe">
                          {bet.timeframe.replace("_", " ")}
                        </div>
                      </div>
                      <div
                        className="bet-status"
                        style={{ gap: 8, flexWrap: "wrap" }}
                      >
                        <span
                          style={{
                            color: "var(--text-secondary)",
                            fontSize: "0.8em",
                          }}
                        >
                          Start: {new Date(bet.createdAt).toLocaleString()}
                        </span>
                        {isFinished ? (
                          <>
                            <span
                              style={{
                                color:
                                  bet.result === "WIN"
                                    ? "var(--accent-success)"
                                    : "var(--accent-error)",
                                fontWeight: 600,
                              }}
                            >
                              {bet.result === "WIN"
                                ? "Win"
                                : bet.result === "LOSS"
                                ? "Loss"
                                : bet.result}
                            </span>
                            <span
                              style={{
                                marginLeft: 8,
                                color: "var(--text-secondary)",
                              }}
                            >
                              Entry:{" "}
                              {bet.entryPrice !== undefined
                                ? `$${Number(bet.entryPrice).toLocaleString(
                                    undefined,
                                    {
                                      minimumFractionDigits: 2,
                                      maximumFractionDigits: 2,
                                    }
                                  )}`
                                : "-"}
                            </span>
                            <span
                              style={{
                                marginLeft: 8,
                                color: priceColor,
                                fontWeight: 600,
                              }}
                            >
                              End:{" "}
                              {bet.exitPrice !== undefined
                                ? `$${Number(bet.exitPrice).toLocaleString(
                                    undefined,
                                    {
                                      minimumFractionDigits: 2,
                                      maximumFractionDigits: 2,
                                    }
                                  )}`
                                : "-"}
                            </span>
                          </>
                        ) : (
                          <>
                            <div className="status-indicator active"></div>
                            <span>Active</span>
                            <span
                              style={{
                                marginLeft: "auto",
                                fontWeight: 600,
                              }}
                            >
                              {getTimeLeft(bet.expiresAt)}
                            </span>
                            <span
                              style={{
                                marginLeft: 8,
                                color: "var(--text-secondary)",
                              }}
                            >
                              Entry:{" "}
                              {bet.entryPrice !== undefined
                                ? `$${Number(bet.entryPrice).toLocaleString(
                                    undefined,
                                    {
                                      minimumFractionDigits: 2,
                                      maximumFractionDigits: 2,
                                    }
                                  )}`
                                : "-"}
                            </span>
                            <span
                              style={{
                                marginLeft: 8,
                                color: priceColor,
                                fontWeight: 600,
                              }}
                            >
                              Current:{" "}
                              {currentPrice !== undefined
                                ? `$${Number(currentPrice).toLocaleString(
                                    undefined,
                                    {
                                      minimumFractionDigits: 2,
                                      maximumFractionDigits: 2,
                                    }
                                  )}`
                                : "-"}
                            </span>
                          </>
                        )}
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="no-bets">
                <svg
                  width="48"
                  height="48"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="1"
                >
                  <path d="M12 2l3.09 6.26L22 9.27l-5 4.87 1.18 6.88L12 17.77l-6.18 3.25L7 14.14 2 9.27l6.91-1.01L12 2z" />
                </svg>
                <p>No active bets</p>
                <span>Place your first bet to get started</span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

