        {betHistoryLoading ? (
          <div className="data-loading">Loading bet history...</div>
        ) : betHistoryData?.betHistory?.length > 0 ? (
          <table className="bet-history-table">
            <thead>
              <tr>
                <th>Crypto</th>
                <th>Direction</th>
                <th>Amount</th>
                <th>Timeframe</th>
                <th>Entry Price</th>
                <th>Exit Price</th>
                <th>Result</th>
                <th>Result Reason</th>
                <th>Payout</th>
                <th>Date</th>
              </tr>
            </thead>
            <tbody>
              {betHistoryData.betHistory.map((bet) => {
                // Determine win/loss display and color
                let resultDisplay = null;
                if (bet.result === "WIN") {
                  resultDisplay = (
                    <span
                      style={{
                        color: "var(--accent-success)", // green for Win
                        fontWeight: 600,
                      }}
                    >
                      Win
                    </span>
                  );
                } else if (bet.result === "LOSS") {
                  resultDisplay = (
                    <span
                      style={{
                        color: "var(--accent-error)", // red for Loss
                        fontWeight: 600,
                      }}
                    >
                      Loss
                    </span>
                  );
                } else {
                  resultDisplay = bet.result || bet.status;
                }

                // Entry and exit price with proper formatting
                const entryPrice = bet.entryPrice
                  ? `$${Number(bet.entryPrice).toLocaleString(undefined, {
                      minimumFractionDigits: 2,
                      maximumFractionDigits: 2,
                    })}`
                  : "-";

                const exitPrice = bet.exitPrice
                  ? `$${Number(bet.exitPrice).toLocaleString(undefined, {
                      minimumFractionDigits: 2,
                      maximumFractionDigits: 2,
                    })}`
                  : "-";

                // Result reason with proper price formatting
                let reason = "";
                if (bet.result === "WIN" || bet.result === "LOSS") {
                  const entryFormatted = bet.entryPrice
                    ? Number(bet.entryPrice).toLocaleString(undefined, {
                        minimumFractionDigits: 2,
                        maximumFractionDigits: 2,
                      })
                    : "N/A";

                  const exitFormatted = bet.exitPrice
                    ? Number(bet.exitPrice).toLocaleString(undefined, {
                        minimumFractionDigits: 2,
                        maximumFractionDigits: 2,
                      })
                    : "N/A";

                  if (bet.betType === "UP") {
                    reason = `Price went ${
                      bet.result === "WIN" ? "up" : "down"
                    } from $${entryFormatted} to $${exitFormatted}`;
                  } else if (bet.betType === "DOWN") {
                    reason = `Price went ${
                      bet.result === "WIN" ? "down" : "up"
                    } from $${entryFormatted} to $${exitFormatted}`;
                  }
                }
                return (
                  <tr
                    key={bet.id}
                    className={
                      bet.result === "WIN"
                        ? "win-row"
                        : bet.result === "LOSS"
                        ? "loss-row"
                        : ""
                    }
                  >
                    <td>{bet.cryptoSymbol}</td>
                    <td>{bet.betType}</td>
                    <td>${bet.amount}</td>
                    <td>{bet.timeframe.replace("_", " ")}</td>
                    <td>{entryPrice}</td>
                    <td>{exitPrice}</td>
                    <td>{resultDisplay}</td>
                    <td>{reason}</td>
                    <td>
                      {bet.payout !== null ? `$${bet.payout.toFixed(2)}` : "-"}
                    </td>
                    <td>{new Date(bet.createdAt).toLocaleString()}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        ) : (
          <div>No bet history yet.</div>
        )}
      </div>
    </div>
  );
}

function App() {
  // Performance monitoring
  usePerformanceMonitor();

  // Optimized state management
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [isRefreshing, setIsRefreshing] = useState(false);
  const [previousPrices, setPreviousPrices] = useState({});
  const [selectedCrypto, setSelectedCrypto] = useState(null);
  const [selectedTimeframe, setSelectedTimeframe] = useState("FIVE_MINUTES");
  const [betAmount, setBetAmount] = useState(100);
  const [betType, setBetType] = useState("UP");
  const [showBetModal, setShowBetModal] = useState(false);
  const [activeTab, setActiveTab] = useState("prices");
  const [searchQuery, setSearchQuery] = useState("");
  const [isMobileNavOpen, setIsMobileNavOpen] = useState(false);
  const [authModalOpen, setAuthModalOpen] = useState(false);
  const [authMode, setAuthMode] = useState("login");
  const [authError, setAuthError] = useState("");
  const [authLoading, setAuthLoading] = useState(false);
  const [user, setUser] = useState(() => {
    const token = localStorage.getItem("token");
    const user = localStorage.getItem("user");
    return token && user ? JSON.parse(user) : null;
  });
  const [token, setToken] = useState(() => localStorage.getItem("token") || "");

  const userId = "user-1";

  // Apollo auth link
  const httpLink = createHttpLink({ uri: "http://localhost:4000/graphql" });
  const authLink = setContext((_, { headers }) => {
    return {
      headers: {
        ...headers,
        authorization: token ? `Bearer ${token}` : "",
      },
    };
  });
  const apolloClient = useMemo(
    () =>
      new ApolloClient({
        link: authLink.concat(httpLink),
        cache: new InMemoryCache(),
      }),
    [token]
  );

  // Register/Login mutations
  const [registerMutation] = useMutation(REGISTER, { client: apolloClient });
  const [loginMutation] = useMutation(LOGIN, { client: apolloClient });

  // Auth handlers
  const handleAuth = async (username, password) => {
    setAuthError("");
    setAuthLoading(true);
    try {
      if (authMode === "register") {
        await registerMutation({ variables: { username, password } });
        setAuthMode("login");
        setAuthLoading(false);
        setAuthError("Registration successful! Please log in.");
        return;
      } else {
        const { data } = await loginMutation({
          variables: { username, password },
        });
        setUser(data.login.user);
        setToken(data.login.token);
        localStorage.setItem("token", data.login.token);
        localStorage.setItem("user", JSON.stringify(data.login.user));
        setAuthModalOpen(false);
      }
    } catch (e) {
      setAuthError(e.message);
    } finally {
      setAuthLoading(false);
    }
  };
  const handleLogout = () => {
    setUser(null);
    setToken("");
    localStorage.removeItem("token");
    localStorage.removeItem("user");
  };

  // Show modal if not logged in
  useEffect(() => {
    if (!user) setAuthModalOpen(true);
  }, [user]);

  // Optimized queries with better caching
  const { loading, error, data, refetch } = useQuery(GET_CRYPTO_PRICES, {
    pollInterval: 30000,
    notifyOnNetworkStatusChange: true,
    errorPolicy: "all",
  });

  const { data: coinsData, loading: coinsLoading } = useQuery(GET_COINS, {
    errorPolicy: "all",
  });

  const { data: userStats } = useQuery(GET_USER_STATS, {
    variables: { userId },
    pollInterval: 10000,
    errorPolicy: "all",
  });

  const { data: activeBetsData } = useQuery(GET_ACTIVE_BETS, {
    variables: { userId },
    pollInterval: 5000,
    errorPolicy: "all",
  });

  const { data: betHistoryData, loading: betHistoryLoading } = useQuery(
    GET_BET_HISTORY,
    {
      variables: { userId },
      pollInterval: 10000,
      errorPolicy: "all",
    }
  );

  const [placeBet] = useMutation(PLACE_BET, {
    refetchQueries: [
      { query: GET_USER_STATS, variables: { userId } },
      { query: GET_ACTIVE_BETS, variables: { userId } },
    ],
    onError: (error) => {
      console.error("Bet placement error:", error);
    },
  });

  // Optimized animated prices with useMemo
  const animatedPrices = useMemo(() => {
    if (!data?.cryptoPrices) return [];
    return data.cryptoPrices.map((crypto) => crypto.price);
  }, [data?.cryptoPrices]);

  // Memoized filtered crypto data
  const filteredCryptoData = useMemo(() => {
    if (!data?.cryptoPrices) return [];
    return data.cryptoPrices.filter(
      (crypto) =>
        searchQuery === "" ||
        crypto.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        crypto.symbol.toLowerCase().includes(searchQuery.toLowerCase())
    );
  }, [data?.cryptoPrices, searchQuery]);

  // Optimized handlers with useCallback
  const handleRefresh = useCallback(async () => {
    performance.mark("refresh-start");
    setIsRefreshing(true);
    try {
      await refetch();
    } catch (error) {
      console.error("Refresh error:", error);
    } finally {
      setTimeout(() => {
        setIsRefreshing(false);
        performance.mark("refresh-end");
        performance.measure("refresh-duration", "refresh-start", "refresh-end");
      }, 1000);
    }
  }, [refetch]);

  const handlePlaceBet = useCallback(async () => {
    try {
      await placeBet({
        variables: {
          input: {
            cryptoSymbol: selectedCrypto,
            betType: betType,
            amount: parseFloat(betAmount),
            timeframe: selectedTimeframe,
          },
        },
      });
      setShowBetModal(false);
      setBetAmount(100);
    } catch (error) {
      console.error("Error placing bet:", error);
      alert(error.message);
    }
  }, [placeBet, selectedCrypto, betType, betAmount, selectedTimeframe]);

  const getPriceChange = useCallback(
    (symbol, currentPrice) => {
      const previousPrice = previousPrices[symbol];
      if (!previousPrice || previousPrice === currentPrice) return null;
      return {
        direction: currentPrice > previousPrice ? "up" : "down",
        percentage: (
          ((currentPrice - previousPrice) / previousPrice) *
          100
        ).toFixed(2),
      };
    },
    [previousPrices]
  );

  // Preload critical resources
  useEffect(() => {
    const preloadImage = new Image();
    preloadImage.src = `${process.env.PUBLIC_URL}/images/backgroundImage.png`;
  }, []);

  // Optimized price tracking
  useEffect(() => {
    if (data?.cryptoPrices) {
      setPreviousPrices((prev) => {
        const newPrices = {};
        data.cryptoPrices.forEach((crypto) => {
          newPrices[crypto.symbol] = prev[crypto.symbol] || crypto.price;
        });
        return newPrices;
      });
    }
  }, [data]);

  // Set default selected crypto when coins load
  useEffect(() => {
    if (!selectedCrypto && coinsData?.coins?.length > 0) {
      setSelectedCrypto(coinsData.coins[0].symbol);
    }
  }, [coinsData, selectedCrypto]);

  // Helper for timer with memoization
  const getTimeLeft = useCallback((expiresAt) => {
    const now = new Date();
    const expiry = new Date(expiresAt);
    const diff = expiry - now;
    if (diff <= 0) return "Expired";
    const min = Math.floor(diff / 60000);
    const sec = Math.floor((diff % 60000) / 1000);
    return `${min}:${sec.toString().padStart(2, "0")}`;
  }, []);

  const getCurrentCrypto = useCallback(() => {
    return data?.cryptoPrices?.find(
      (crypto) => crypto.symbol === selectedCrypto
    );
  }, [data, selectedCrypto]);

  const getSelectedTimeframeInfo = useCallback(() => {
    return TIMEFRAMES.find((tf) => tf.value === selectedTimeframe);
  }, [selectedTimeframe]);

  // Timer update for Active Bets
  const [, setTimerTick] = useState(0);
  useEffect(() => {
    const interval = setInterval(() => {
      setTimerTick((tick) => tick + 1);
    }, 1000);
    return () => clearInterval(interval);
  }, []);

  // Error handling
  if (error && !data) {
    return (
      <div className={`app ${isDarkMode ? "dark" : "light"}`}>
        <div className="error-container">
          <div className="kokitzu-logo">Kokitzu</div>
          <div className="error-message">
            <h2>Connection Error</h2>
            <p>{error.message}</p>
            <button onClick={handleRefresh} className="retry-button">
              Retry Connection
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <ApolloProvider client={apolloClient}>
      {authModalOpen && (
        <AuthModal
          mode={authMode}
          setMode={setAuthMode}
          onSubmit={handleAuth}
          error={authError}
          loading={authLoading}
        />
      )}
      <Router>
        <ErrorBoundary>
          <div style={backgroundStyle}>
            <div
              style={{
                position: "fixed",
                top: 0,
                left: 0,
                width: "100vw",
                height: "100vh",
                zIndex: 0,
                pointerEvents: "none",
                background: isDarkMode
                  ? "rgba(0,0,0,0.5)"
                  : "rgba(255,255,255,0.9)",
              }}
            />
            {/* <KokitzuBackground /> */}
            <div className={`app ${isDarkMode ? "dark" : "light"}`}>
              {/* Navigation */}
              <nav className="nav">
                <div className="nav-content">
                  <div className="nav-left">
                    <div className="kokitzu-logo">Kokitzu</div>
                    <div className="brand-subtitle">Crypto Options Trading</div>
                  </div>
                  <div className="nav-divider" />
                  <div className="nav-right">
                    <button
                      className={`refresh-button ${
                        isRefreshing ? "refreshing" : ""
                      }`}
                      onClick={handleRefresh}
                      title="Refresh data"
                    >
                      <svg
                        width="18"
                        height="18"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                      >
                        <path d="M23 4v6h-6M1 20v-6h6M20.49 9A9 9 0 0 0 5.64 5.64L1 10m22 4l-4.64 4.36A9 9 0 0 1 3.51 15" />
                      </svg>
                    </button>

                    <button
                      className="theme-toggle"
                      onClick={() => setIsDarkMode(!isDarkMode)}
                      title={`Switch to ${isDarkMode ? "light" : "dark"} mode`}
                    >
                      {isDarkMode ? (
                        <svg
                          width="18"
                          height="18"
                          viewBox="0 0 24 24"
                          fill="none"
                          stroke="currentColor"
                          strokeWidth="2"
                        >
                          <circle cx="12" cy="12" r="5" />
                          <path d="M12 1v2M12 21v2M4.22 4.22l1.42 1.42M18.36 18.36l1.42 1.42M1 12h2M21 12h2M4.22 19.78l1.42-1.42M18.36 5.64l1.42-1.42" />
                        </svg>
                      ) : (
                        <svg
                          width="18"
                          height="18"
                          viewBox="0 0 24 24"
                          fill="none"
                          stroke="currentColor"
                          strokeWidth="2"
                        >
                          <path d="M21 12.79A9 9 0 1 1 11.21 3 7 7 0 0 0 21 12.79z" />
                        </svg>
                      )}
                    </button>

                    <div className="user-profile">
                      <div className="user-avatar">ðŸ‘¤</div>
                      <div className="user-info">
                        <span className="username">
                          {user ? user.username : "Guest"}
                        </span>
                        <span className="user-balance">
                          ${user ? user.balance.toLocaleString() : "-"}
                        </span>
                      </div>
                    </div>
                    {user && (
                      <button
                        className="logout-btn"
                        onClick={handleLogout}
                        title="Logout"
                      >
                        Logout
