function PerformanceChart({ bets }) {
  // Sort bets by date
  const sorted = [...bets].sort(
    (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
  );
  // Calculate cumulative profit
  let cumulative = 0;
  const points = sorted.map((bet, i) => {
    const profit = bet.result === "WIN" ? bet.payout - bet.amount : -bet.amount;
    cumulative += profit;
    return { x: i, y: cumulative, date: bet.createdAt, profit };
  });
  if (points.length < 2) {
    // Not enough data for a line
    return (
      <svg width="100%" height="300" viewBox="0 0 400 300">
        <text x="50%" y="50%" textAnchor="middle" fill="#888" fontSize="18">
          Not enough data
        </text>
      </svg>
    );
  }
  // Scale points to SVG
  const maxY = Math.max(...points.map((p) => p.y), 0);
  const minY = Math.min(...points.map((p) => p.y), 0);
  const rangeY = maxY - minY || 1;
  const chartW = 380,
    chartH = 260,
    padX = 10,
    padY = 20;
  const scaleX = (i) => padX + i * (chartW / (points.length - 1));
  const scaleY = (y) => padY + chartH - ((y - minY) / rangeY) * chartH;
  const pathD = points
    .map((p, i) => `${i === 0 ? "M" : "L"}${scaleX(i)},${scaleY(p.y)}`)
    .join(" ");
  return (
    <svg width="100%" height="300" viewBox={`0 0 400 300`}>
      <defs>
        <linearGradient id="chartGradient" x1="0%" y1="0%" x2="0%" y2="100%">
          <stop
            offset="0%"
            stopColor="var(--accent-primary)"
            stopOpacity="0.3"
          />
          <stop
            offset="100%"
            stopColor="var(--accent-primary)"
            stopOpacity="0"
          />
        </linearGradient>
      </defs>
      <path
        d={pathD}
        stroke="var(--accent-primary)"
        strokeWidth="3"
        fill="none"
        className="chart-line"
      />
      <polyline
        points={points.map((p, i) => `${scaleX(i)},${scaleY(p.y)}`).join(" ")}
        fill="url(#chartGradient)"
        opacity="0.1"
        className="chart-area"
      />
      {points.map((p, i) => (
        <circle
          key={i}
          cx={scaleX(i)}
          cy={scaleY(p.y)}
          r="4"
          className="data-point"
        />
      ))}
      {/* Y axis labels */}
      <text x={padX} y={scaleY(maxY) - 5} fill="#aaa" fontSize="12">
        {maxY.toFixed(2)}
      </text>
      <text x={padX} y={scaleY(minY) + 15} fill="#aaa" fontSize="12">
        {minY.toFixed(2)}
      </text>
    </svg>
  );
}

// Auth Modal Component
function AuthModal({ mode, setMode, onSubmit, error, loading }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  return (
    <div className="modal-overlay" style={{ zIndex: 2000 }}>
      <div className="modal-content" style={{ maxWidth: 340 }}>
        <h3>{mode === "login" ? "Login" : "Register"}</h3>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            onSubmit(username, password);
          }}
        >
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            autoFocus
            style={{ width: "100%", marginBottom: 12 }}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            style={{ width: "100%", marginBottom: 12 }}
            required
          />
          {error && (
            <div style={{ color: "var(--accent-error)", marginBottom: 8 }}>
              {error}
            </div>
          )}
          <button
            type="submit"
            className="place-bet-btn"
            style={{ width: "100%" }}
            disabled={loading}
          >
            {loading ? "Loading..." : mode === "login" ? "Login" : "Register"}
          </button>
        </form>
        <div style={{ marginTop: 12, textAlign: "center" }}>
          {mode === "login" ? (
            <>
              Don't have an account?{" "}
              <button
                className="tab-button"
                style={{ padding: 0 }}
                onClick={() => setMode("register")}
              >
                Register
              </button>
            </>
          ) : (
            <>
              Already have an account?{" "}
              <button
                className="tab-button"
                style={{ padding: 0 }}
                onClick={() => setMode("login")}
              >
                Login
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}
