import React, { useState } from "react";
import { useWallet } from "../contexts/WalletContext";
import { useNetwork, NETWORKS } from "../contexts/NetworkContext";

const WalletConnectButton = () => {
  const {
    walletAddress,
    isConnected,
    balance,
    connectMetaMask,
    disconnectWallet,
    connectionStatus,
    isMetaMaskInstalled,
  } = useWallet();

  const { currentNetwork, networkConfig, switchNetwork, isNetworkSwitching } =
    useNetwork();
  const [showModal, setShowModal] = useState(false);
  const [showNetworkModal, setShowNetworkModal] = useState(false);

  const handleConnect = async () => {
    try {
      if (!isMetaMaskInstalled()) {
        alert(
          "MetaMask is not installed. Please install MetaMask to continue."
        );
        return;
      }
      await connectMetaMask();
      setShowModal(false);
    } catch (error) {
      console.error("Connection error:", error);
      alert(error.message);
    }
  };

  const handleDisconnect = () => {
    disconnectWallet();
  };

  const handleNetworkSelect = async (networkType) => {
    try {
      setShowNetworkModal(false);
      await switchNetwork(networkType);
    } catch (error) {
      console.error("Network switch error:", error);
      alert(`Failed to switch to ${networkType}: ${error.message}`);
    }
  };

  const formatAddress = (address) => {
    if (!address) return "Unknown";
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  const getNetworkIcon = (network) => {
    switch (network) {
      case "mainnet":
        return "üîµ";
      case "sepolia":
        return "üß™";
      case "goerli":
        return "üß¨";
      default:
        return "üåê";
    }
  };

  const getNetworkColor = (network) => {
    switch (network) {
      case "mainnet":
        return "#10b981";
      case "sepolia":
        return "#3b82f6";
      case "goerli":
        return "#f59e0b";
      default:
        return "#666";
    }
  };

  const getChainName = (chainId) => {
    switch (chainId) {
      case "1":
        return "ETH";
      case "11155111":
        return "Sepolia ETH";
      case "5":
        return "Goerli ETH";
      default:
        return networkConfig.nativeCurrency.symbol;
    }
  };

  // Connected state
  if (isConnected && walletAddress) {
    return (
      <div className="wallet-connect-button connected">
        <div className="wallet-info">
          <div className="wallet-address">{formatAddress(walletAddress)}</div>
          {balance && (
            <div className="wallet-balance">
              {parseFloat(balance).toFixed(4)}{" "}
              {getChainName(networkConfig.chainId)}
            </div>
          )}
        </div>
        <button
          className="disconnect-btn"
          onClick={handleDisconnect}
          title="Disconnect Wallet"
        >
          üîå
        </button>
      </div>
    );
  }

  // Disconnected state
  return (
    <>
      <button
        className="wallet-connect-button"
        onClick={() => setShowModal(true)}
        disabled={connectionStatus === "connecting"}
      >
        {connectionStatus === "connecting" ? "Connecting..." : "Connect Wallet"}
      </button>

      {/* Wallet Connection Modal */}
      {showModal && (
        <div
          className="wallet-modal-overlay"
          onClick={() => setShowModal(false)}
        >
          <div
            className="wallet-modal-content"
            onClick={(e) => e.stopPropagation()}
          >
            {/* Modal Header */}
            <div className="wallet-modal-header">
              <div className="wallet-modal-title">
                <div className="wallet-icon">üîó</div>
                <h3>Connect Your Wallet</h3>
              </div>
              <button
                className="wallet-close-btn"
                onClick={() => setShowModal(false)}
                aria-label="Close modal"
              >
                <svg
                  width="20"
                  height="20"
                  viewBox="0 0 24 24"
                  fill="none"
                  stroke="currentColor"
                  strokeWidth="2"
                >
                  <path d="M18 6L6 18M6 6l12 12" />
                </svg>
              </button>
            </div>

            {/* Network Selection Section */}
            <div className="wallet-network-section">
              <div className="wallet-section-title">
                <span className="section-icon">üåê</span>
                <h4>Select Network</h4>
              </div>
              {!showNetworkModal ? (
                <div
                  className="wallet-network-selector"
                  style={{ borderColor: getNetworkColor(currentNetwork) }}
                  onClick={() => setShowNetworkModal(true)}
                >
                  <div className="wallet-network-info">
                    <div
                      className="wallet-network-icon"
                      style={{ color: getNetworkColor(currentNetwork) }}
                    >
                      {getNetworkIcon(currentNetwork)}
                    </div>
                    <div className="wallet-network-details">
                      <div className="wallet-network-name">
                        {networkConfig.name}
                      </div>
                      <div className="wallet-network-chain">
                        Chain ID: {networkConfig.chainId}
                      </div>
                    </div>
                  </div>
                  <div className="wallet-network-actions">
                    {isNetworkSwitching && (
                      <div className="wallet-loading-spinner">
                        <svg
                          width="16"
                          height="16"
                          viewBox="0 0 24 24"
                          fill="none"
                          stroke="currentColor"
                          strokeWidth="2"
                        >
                          <path d="M21 12a9 9 0 11-6.219-8.56" />
                        </svg>
                      </div>
                    )}
                    <svg
                      className="wallet-chevron"
                      width="16"
                      height="16"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M6 9l6 6 6-6" />
                    </svg>
                  </div>
                </div>
              ) : (
                <div className="wallet-network-list-container">
                  <div className="wallet-network-list-header">
                    <h4>Choose Network</h4>
                    <button
                      className="wallet-close-btn"
                      onClick={() => setShowNetworkModal(false)}
                    >
                      <svg
                        width="16"
                        height="16"
                        viewBox="0 0 24 24"
                        fill="none"
                        stroke="currentColor"
                        strokeWidth="2"
                      >
                        <path d="M18 6L6 18M6 6l12 12" />
                      </svg>
                    </button>
                  </div>
                  <div className="wallet-network-list">
                    {Object.entries(NETWORKS).map(([key, network]) => (
                      <div
                        key={key}
                        className={`wallet-network-item ${
                          currentNetwork === key ? "selected" : ""
                        }`}
                        onClick={() => handleNetworkSelect(key)}
                      >
                        <div className="wallet-network-item-info">
                          <div
                            className="wallet-network-item-icon"
                            style={{ color: getNetworkColor(key) }}
                          >
                            {getNetworkIcon(key)}
                          </div>
                          <div className="wallet-network-item-details">
                            <div className="wallet-network-item-name">
                              {network.name}
                            </div>
                            <div className="wallet-network-item-chain">
                              Chain ID: {network.chainId}
                            </div>
                            {network.isTestnet && (
                              <span className="wallet-testnet-badge">
                                TESTNET
                              </span>
                            )}
                          </div>
                        </div>
                        {currentNetwork === key && (
                          <div
                            className="wallet-check-icon"
                            style={{ color: getNetworkColor(key) }}
                          >
                            <svg
                              width="20"
                              height="20"
                              viewBox="0 0 24 24"
                              fill="none"
                              stroke="currentColor"
                              strokeWidth="2"
                            >
                              <path d="M20 6L9 17l-5-5" />
                            </svg>
                          </div>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>

            {/* Connection Options */}
            <div className="wallet-connection-section">
              <div className="wallet-section-title">
                <span className="section-icon">ü¶ä</span>
                <h4>Connect Wallet</h4>
              </div>
              <div className="wallet-connection-options">
                <button
                  className="wallet-connection-option"
                  onClick={handleConnect}
                  disabled={connectionStatus === "connecting"}
                >
                  <div className="wallet-option-icon">
                    <svg width="32" height="32" viewBox="0 0 24 24" fill="none">
                      <path
                        d="M21 12a9 9 0 11-18 0 9 9 0 0118 0z"
                        fill="#F6851B"
                      />
                      <path
                        d="M12 3a9 9 0 100 18 9 9 0 000-18z"
                        fill="#F6851B"
                      />
                      <path
                        d="M12 6a6 6 0 100 12 6 6 0 000-12z"
                        fill="#F6851B"
                      />
                      <path d="M12 9a3 3 0 100 6 3 3 0 000-6z" fill="#F6851B" />
                    </svg>
                  </div>
                  <div className="wallet-option-details">
                    <div className="wallet-option-title">MetaMask</div>
                    <div className="wallet-option-subtitle">
                      Connect with MetaMask browser extension
                    </div>
                  </div>
                  <div className="wallet-option-arrow">
                    <svg
                      width="16"
                      height="16"
                      viewBox="0 0 24 24"
                      fill="none"
                      stroke="currentColor"
                      strokeWidth="2"
                    >
                      <path d="M5 12h14M12 5l7 7-7 7" />
                    </svg>
                  </div>
                </button>
              </div>
            </div>

            {/* Footer */}
            <div className="wallet-modal-footer">
              <p className="wallet-footer-text">
                By connecting your wallet, you agree to our Terms of Service and
                Privacy Policy
              </p>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default WalletConnectButton;
