import React, { useState, useEffect } from "react";
import { useWallet } from "../contexts/WalletContext";
import { useNetwork, NETWORKS } from "../contexts/NetworkContext";

const WalletConnectButton = () => {
  const {
    walletAddress,
    isConnected,
    balance,
    connectMetaMask,
    disconnectWallet,
    connectionStatus,
    isMetaMaskInstalled,
  } = useWallet();

  const { currentNetwork, networkConfig, switchNetwork, isNetworkSwitching } =
    useNetwork();
  const [showModal, setShowModal] = useState(false);
  const [showNetworkModal, setShowNetworkModal] = useState(false);

  // Handle modal open/close with body scroll lock
  const handleModalOpen = () => {
    setShowModal(true);
    document.body.classList.add("modal-open");
  };

  const handleModalClose = () => {
    setShowModal(false);
    setShowNetworkModal(false);
    document.body.classList.remove("modal-open");
  };

  // Cleanup body class on unmount
  useEffect(() => {
    return () => {
      document.body.classList.remove("modal-open");
    };
  }, []);

  const handleConnect = async () => {
    try {
      if (!isMetaMaskInstalled()) {
        alert(
          "MetaMask is not installed. Please install MetaMask to continue."
        );
        return;
      }
      await connectMetaMask();
      setShowModal(false);
    } catch (error) {
      console.error("Connection error:", error);
      alert(error.message);
    }
  };

  const handleDisconnect = () => {
    disconnectWallet();
  };

  const handleNetworkSelect = async (networkType) => {
    try {
      setShowNetworkModal(false);
      await switchNetwork(networkType);
    } catch (error) {
      console.error("Network switch error:", error);
      alert(`Failed to switch to ${networkType}: ${error.message}`);
    }
  };

  const formatAddress = (address) => {
    if (!address) return "Unknown";
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  const getNetworkIcon = (network) => {
    switch (network) {
      case "mainnet":
        return "ðŸ”µ";
      case "sepolia":
        return "ðŸ§ª";
      case "goerli":
        return "ðŸ§¬";
      default:
        return "ðŸŒ";
    }
  };

  const getNetworkColor = (network) => {
    switch (network) {
      case "mainnet":
        return "#10b981";
      case "sepolia":
        return "#3b82f6";
      case "goerli":
        return "#f59e0b";
      default:
        return "#666";
    }
  };

  const getChainName = (chainId) => {
    switch (chainId) {
      case "1":
        return "ETH";
      case "11155111":
        return "Sepolia ETH";
      case "5":
        return "Goerli ETH";
      default:
        return networkConfig.nativeCurrency.symbol;
    }
  };

  // Connected state
  if (isConnected && walletAddress) {
    return (
      <>
        <div className="wallet-connect-container">
          <button
            className="wallet-connect-btn connected"
            onClick={handleModalOpen}
            title="Wallet Connected - Click to manage"
          >
            <span className="wallet-icon">ðŸ¦Š</span>
            <div className="wallet-info">
              <div className="wallet-address">
                {formatAddress(walletAddress)}
              </div>
              {balance && (
                <div className="wallet-balance">
                  {parseFloat(balance).toFixed(4)}{" "}
                  {getChainName(networkConfig.chainId)}
                </div>
              )}
            </div>
            <div
              className={`network-indicator ${
                networkConfig.isTestnet ? "testnet" : "mainnet"
              }`}
            ></div>
          </button>
          <button
            className="wallet-disconnect-quick"
            onClick={handleDisconnect}
            title="Disconnect Wallet"
          >
            <svg
              width="16"
              height="16"
              viewBox="0 0 24 24"
              fill="none"
              stroke="currentColor"
              strokeWidth="2"
            >
              <path d="M9 21H5a2 2 0 0 1-2-2V5a2 2 0 0 1 2-2h4" />
              <polyline points="16,17 21,12 16,7" />
              <line x1="21" y1="12" x2="9" y2="12" />
            </svg>
          </button>
        </div>

        {/* Connected Wallet Modal */}
        {showModal && (
          <div className="wallet-modal-overlay" onClick={handleModalClose}>
            <div
              className="wallet-modal-content"
              onClick={(e) => e.stopPropagation()}
            >
              {/* Modal Header */}
              <div className="wallet-modal-header">
