              <tr>
                <th>Crypto</th>
                <th>Direction</th>
                <th>Amount</th>
                <th>Timeframe</th>
                <th>Entry Price</th>
                <th>Exit Price</th>
                <th>Result</th>
                <th>Result Reason</th>
                <th>Payout</th>
                <th>Date</th>
